import React, { useRef, useEffect, useState } from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, event, bisector, mouse, ticks} from 'd3';
import useResizeObserver from "./useReziseObserver";


function BarMobilNutzung({ data, highlight }) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);
   const [selection, setSelection] = useState([0, 1.5]);

   useEffect(() => {

    const svg = select(svgRef.current);
     
         if(!dimensions) return;
      
         const xScale = scaleLinear()
         .domain([data.age.map((value, index) => index)])
         
       // .domain([0, data.length-1]) //0 zu 0 ; 6 zu 200
        .range([0, dimensions.width]) //change
       
        const yScale =  scaleLinear()
        .domain([0, 100])   //todo
        .range([dimensions.height, 30]);  //change

        const yAxis = axisLeft(yScale);   
        svg
        .select('.y-axis')
        .style('color', 'grey')
        .style('font-size', '14')
        .call(yAxis);
  
        //X-Achsen-Beschriftung
        svg.append("text")
        .attr("text-anchor", "end")
        .attr("fill", "grey")
        .attr("x", dimensions.width)
         .attr("y", dimensions.height  + 50)
         .text("Jahr");



  }, [data, dimensions]);
  return (
   <div ref={wrapperRef} style={{ marginBottom: "2rem" }}>
      <svg ref={svgRef}>
        <g className="x-axis" />
        <g className="y-axis" />
      </svg>
    </div>
);
}

export default BarMobilNutzung;
