import React, { useEffect, useState } from "react";
import AreaChartContainer from './IKTBeschäftigungChart'
import Card from '../Cards'
import '../../styles/charts.css'


export default function Nutzungsgründe() {
  
    const [data, setData] = useState([]);
    const [id, setID] = useState(["IKTGen"]);
    const [value1, setValue1] = useState(["Männer"]);
    const [value2, setValue2] = useState(["Frauen"]);
    const [color1, setColor1] = useState(["#6810d4"]);
    const [color2, setColor2] = useState(["#d043ee"]);
    const [property, setProperty] = useState("Europäische Union");

    const handleFirst = () => {
        setValue1("Männer");
        setValue2("Frauen");
        setColor1("#6810d4");
        setColor2("#d043ee");
        setID("IKTGen")
      } 
     
      const handleSecond = () => {
        setValue1("Beschäftigung");
        setValue2("Einstellung");
        setColor1("#203040");
        setColor2("#2671bb");
        setID("IKTBesch")
      } 

    useEffect(() => {
        fetch(`https://webhooks.mongodb-stitch.com/api/client/v2.0/app/dataviztest-bbcvc/service/viz/incoming_webhook/${id}`)
         .then(response => response.ok && response.json())
         .then(dataResp => {
           setData(
              dataResp
              //.sort((a, b) => a.time.localeCompare(b.time))
            );
          })
          .catch(console.error);
      }, [id, value1, value2, color1, color2]);


    return (
            <div className="chart4">
                <div className="IKTWrapping">
                        <div className="IKTHeadLine">
                            <div className="IKTgHead">
                                <h1>IKT Beschäftigung</h1>
                            </div>
                            <div className="IKTText">
                                <p>
                                Informations- und Kommunikationstechnologien sind für Unternehmen in den letzten Jahren immer wichtiger geworden. Die Nutzung von IKT für ihren täglichen Betrieb erfordert häufig die Entwicklung und Wartung von IKT-Systemen durch Spezialisten. 
                                <br></br> <br></br>Im Jahr 2018 waren 83,5% der IKT-Spezialisten Männer, deren Anteil verglichen mit 2010 (78,3%) anstieg. In allen EU-Ländern waren weibliche IKT-Spezialisten unterrepräsentiert.
                                Die größten geschlechtsspezifischen Unterschiede sind in Tschechien (Männer 90,1%) und Ungarn (Männer 91,5%) zu verzeichnen. Den größten Anteil an weiblichen IKT-Spezialisten wurden in Bulgarien (28,3%) und in Litauen (25,4%) registriert.
                                <br></br> <br></br>
                               Im Jahr 2019 beschäftigte rund ein Fünftel der Unternehmen (20%) in der EU IKT-Spezialisten. Dieser Anteil ist seit 2012 ungefähr gleich groß. Dänemark (30%) und Irland (32%) beschäftigten 2019 am meisten IKT-Spezialisten.
                               9% der Unternehmen in der EU gaben an, IKT-Spezialisten eingestellt oder versucht zu haben, diese einzustellen. Dieser Anteil war am größten in Belgien, Dänemark und Luxemburg mit 14%.
                                </p>
                            </div>
                        </div>
                        <select value={property} onChange={e => setProperty(e.target.value)}>
                            {data.map(dataZugang => (
                            <option key={dataZugang.geo}>{dataZugang.geo}</option>
                            ))}
                        </select>
                        </div>
                        <div className="ChartIKTWrap">
                            <div className="ChartIKT">
                            <AreaChartContainer property={property} data={data} value1={value1} value2={value2} color1={color1} color2={color2}></AreaChartContainer>
                            </div>
                        <div className="ChartIKTCards">
                            <Card handle={handleFirst} 
                            headCard="IKT-Spezialisten nach Geschlecht"
                            classFirst="dataRecordM"
                            classFirst="dataRecordF"
                            firstValue="Männer"
                            secondValue="Frauen"
                            cardText=" Diese Visualisierung zeigt die Unterschiede bei den erwerbstätigen IKT-Spezialisten aufgeschlüsselt nach Geschlecht."></Card>
                            <Card handle={handleSecond} 
                            headCard="IKT-Beschäftigung in Unternehmen"
                            firstValue="beschäftigt"
                            secondValue="eingestellt"
                            cardText=" Die Visualisierung zeigt den Anteil von Unternehmen, die IKT-Fachkräfte versucht haben einzustellen oder tatsächlich eingestellt haben. Die Daten beziehen sich auf alle Unternehmen mit 10 Beschäftigten oder mehr (ohne Bankensektor)."></Card>
                            <button className="Cards" onClick={handleSecond}>
                                <p className="headIKT">IKT-Beschäftigung</p>
                                <div className="DataWrap">
                                    <div className="dataRecordBesch">beschäftigt</div>
                                    <div className="dataRecordBes">eingestellt</div>
                                </div>
                                <p className="CardText">
                                
                                </p>
                            </button>
                            <div></div>
                        </div>
                        </div>
            </div>

    );
}
