import React, { useEffect, useRef } from "react";
import PropTypes from 'prop-types'
import { select, geoPath, geoMercator } from "d3";
import useResizeObserver from './useReziseObserver'
import '../../styles/header.css'

function GeoChart({ data, property }){
    const svgRef = useRef();
    const wrapperRef = useRef();
    const dimensions = useResizeObserver(wrapperRef);
    
    useEffect(() => {
        const svg = select(svgRef.current);



       // const { width, height } =
          //  dimensions || wrapperRef.current.getCoundingClientRect();
            // Projiziert die Geo-Koordinaten in 2D
            const projection = geoMercator();

            //nimmt Geo-Daten als Input und übersetzt sie in d attributes eines path elements
            const pathGenerator = geoPath().projection(projection);

            svg
            .selectAll(".country")
            .data(data.features)
            .join("path")
            .attr("class", "country")
            .attr("d", feature => pathGenerator(feature));

    }, [data, dimensions, property]);

    return (
        <div ref={wrapperRef} style={{ marginBottom: "2rem" }}>
            <svg ref={svgRef}>
            </svg>
            </div>
   );
}

export default GeoChart;
