import React, { useRef, useEffect} from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, min, max, bisector, mouse, entries} from 'd3';
import '../../styles/charts.css'
import useResizeObserver from './useReziseObserver'

function LineCharts({ data, property, colorline, colorline2, value}) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);
   useEffect(() => {
      var value1 = "Alle"
      var value2 = "Breitband"
      const svg = select(svgRef.current);
      var filterAlle;
      var filterBB;

         if(!dimensions) return;
      
      //Daten filtern
         var filtered = data.filter(a=>a.geo==property);         
            for(var i = 0; i < filtered.length; i ++) {
             var filterAlle = filtered[i].alle; 
            }

            for(var i = 0; i < filtered.length; i ++) {
               var filterBB = filtered[i].bb; 
                }
            var valesAlle = entries(filterAlle);
            var valesBB = entries(filterBB);

      //Sortieren nach Jahreszahl
      const minDate = new Date (min(valesAlle, value => value.key));
      const maxDate = new Date (max(valesAlle, value => value.key));     

     // console.log(minDate);
     // console.log(maxDate);
       

      const xScale = scaleTime()
          .domain([minDate - 2.628e+9,maxDate])   
          .range([-1, dimensions.width]) 
        
      const yScale =  scaleLinear()
            .domain([0, 100])
             .range([dimensions.height, 0]);

      
      const yAxis = axisLeft(yScale).tickSize(0);   
      svg
      .select('.y-axis')
      .style('color', 'grey')
      .style('font-size', '14')
      .call(yAxis);

     const xAxis = axisBottom(xScale)
     svg
      .select(".x-axis")
      .join(".x-axis")
      .style('transform', `translateY(${dimensions.height}px)`)
      .style('color', 'grey')
      .style('font-size', '14')
      .style('stroke-width', 1)
      .call(xAxis);

      //Grids
      var grids = svg.select("g").append("g")
         .join("g")
         .attr("class", "grid")
         .attr('transform', 'translate(0,' + -dimensions.height + ')')
         .call(axisLeft(yScale)
            .tickSize(-dimensions.width)
            .tickFormat("")
            )
      
      //X-Achsen-Beschriftung
      svg.select("text").join("text")
      .attr("text-anchor", "end")
      .attr("fill", "grey")
      .attr("x", dimensions.width)
       .attr("y", + dimensions.height/ 8)
       .attr("font-weight", "bold")
       .text("Jahr");      
      //Y-Achsen-Beschrifzung
      svg.append("text")
      .attr("text-anchor", "end")
      .attr("transform", "rotate(-90)")
      .attr("fill", "grey")
      .attr("y", - 50)
      .attr("x", 0)
      .text("Prozent")

      tooltip(svg, valesAlle, valesBB, color1, colorline, colorline2, xScale, yScale, value1, value2);
    //Lines
      const myLine = line()
      .x((d)=> (xScale(new Date(d.key))))
      .y((d)=> (yScale(d.value)))
      .defined((d)=> d.value != 0)
      .curve(curveCardinal);
    
      //herausfiltern der fehlenden werte
      var filteredAlle = valesAlle.filter(myLine.defined());
      var filteredBB = valesBB.filter(myLine.defined());
      
         var lineAlle1 = svg 
         .selectAll(".newline")
         .data([filteredAlle])
         .join("path")
         .attr("class", "newline")
         .attr("d", (value, index) => myLine(value))
         .transition()
         .attr("fill", "none")
         .attr("stroke", colorline)
         .attr("stroke-dasharray", ("3, 3"))
        
         var lineAlle2 =    svg
         .selectAll(".newerline")
         .data([valesAlle])
         .join("path")
         .attr("class", "newerline")
         .attr("d", (value, index) => myLine(value))
         .transition()
         .attr("fill", "none")
         .attr("stroke", colorline)
         .attr("stroke-width", "5" )
         .attr("stroke-linecap", "round")

         var lineBB1 = svg
         .selectAll(".newlineBB")
         .data([filteredBB])
         .join("path")
         .attr("class", "newlineBB")
         .attr("d", (value, index) => myLine(value))
         .transition()
         .attr("fill", "none")
         .attr("stroke", colorline2)
         .attr("stroke-dasharray", ("3, 3"))
         .attr("opacity", "0")    
        
         var lineBB2 = svg
         .selectAll(".newerlineBB")
         .data([valesBB])
         .join("path")
         .attr("class", "newerlineBB")
         .attr("d", (value, index) => myLine(value))
         .transition()
         .attr("fill", "none")
         .attr("stroke", colorline2)
         .attr("stroke-width", "5" )
         .attr("stroke-linecap", "round")
         .attr("opacity", "0")

         if(value == "BB"){
            lineBB1.attr("opacity", "1")
            lineBB2.attr("opacity", "1")
         }
         else{
            console.log("noooo")
         }
    //     } else {
    //        console.log("Nooo")
    //     }
      //Tooltip
      //Tooltip-Elemente
     var focusBox = svg
     .append('g')
     .append('rect')
     .style("opacity", 0)
     .attr("width", 90)
     .attr("height", 30)
     .style("fill", "#e0e2e5")
     .style("stroke", "#e0e2e5")
     .style("stroke", colorline)
     .style("stroke-width", "1" )
     .attr("rx", 4)
           .attr("ry", 4);

     var focus = svg
     .append('g')
     .append('circle')
     .style("fill", colorline)
     .attr('r', 8.5)
     .style("opacity", 0);

     var focusText = svg
     .append('g')
     .append('text')
     .style("opacity", 0)
     .attr("text-anchor", "left")
     .attr("alignment-baseline", "middle")
     .attr("fill", "#203040")
     .style("font-size", 13)
     .style("font-weight", "bold")
     
     svg
     .append('rect')
     .style("fill", "none")
     .style("pointer-events", "all")
     .attr('width', dimensions.width)
     .attr('height', dimensions.height)
     .on('mouseover', mouseover)
     .on('mousemove', mousemove)
     .on('mouseout', mouseout); 
     
    
      function mouseover() {
         focus.style("opacity", 1)
         focusText.style("opacity",1)
         focusBox.style("opacity",1)
       }
       
       function mousemove() {
         var x0 = xScale.invert(mouse(this)[0]);
         const bisect = bisector(d => new Date(d.key)).left;
         const idx = bisect(filteredAlle, x0, 1);
         const selectedData = filteredAlle[idx];
           
       focus
         .transition()
         .duration(50)
         .attr("cx", xScale(new Date(selectedData.key)))
         .attr("cy", yScale(selectedData.value))
      
         focusText
         .transition()
         .duration(50)
            .text(selectedData.key + ": " + selectedData.value + "%")
            .attr("x", xScale(new Date (selectedData.key)))
            .attr("y", yScale(selectedData.value) - 30)
            
         focusBox
         .transition()
         .duration(50)
            .attr("x", xScale(new Date (selectedData.key))- 15)
            .attr("y", yScale(selectedData.value) - 45)

         }
       function mouseout() {
         focus.style("opacity", 0)
         focusText.style("opacity", 0)
         focusBox.style("opacity", 0)
       }


   }, [data, dimensions, property]);
   return (
         <div ref={wrapperRef}>
            <svg ref={svgRef}>
               <g className='x-axis' />
               <g className="y-axis" />
            </svg>
            </div>
   );
}

export default LineCharts;
