import { select, min, max } from "d3";
import useResizeObserver from './useReziseObserver'
import '../../styles/charts.css'

const getData = dateString => {
    const date = dateString.split("-");
    return new Date(date[2], date[0] - 1, date[1]);
}

export default function LivingOnline({ data, highlight }) {
    const svgRef = useRef();
    const wrapperRef = useRef();
    const dimensions = useResizeObserver(wrapperRef);

    useEffect(() => {
        const svg = select(svgRef.current);
            if(!dimensions) return;

            const minDate = min(data, episode => getData(episode.air_date))
    }, [data,dimensions, highlight]);


    return (
         <div ref={wrapperRef}>
            <svg ref={svgRef}>
               <g className='x-axis' />
               <g className="y-axis" />
            </svg>
            </div>
   );

}
