import React, { useRef, useEffect, useState} from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, event, bisector, mouse, ticks, keys} from 'd3';
import '../../styles/charts.css'
import useResizeObserver from './useReziseObserver'

function LineCharts({ data, property }) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);
   const [selectedCountry, setSelectedCountry] = useState(null);
  // const [dat, setData] = useState([25, 30, 45, 60, 20, 65, 75]);
   //Wird einmal aufgerufen, wenn DOM-Element gerendert wird, und wenn sich Elemente im Array verÃ¤ndern
   useEffect(() => {

      const svg = select(svgRef.current);
         console.log(property);
      //const test = data.value[0];
     // console.log(test);
         if(!dimensions) return;
      
         var filtered = data.filter(a=>a.geo==property);
       //  console.log(filtered);
       var timing = a=>a.time;
       console.log(timing);

      //Sortieren nach Jahreszah
      const minDate = new Date (min(filtered, value => Object.keys(value.time)[0]));
      const maxDate = new Date (max(filtered, value => Object.keys(value.time)[3]));

      

      console.log(minDate);
      console.log(maxDate);


      const xScale = scaleTime()
          .domain([minDate,maxDate])
          
        // .domain([0, data.length-1]) //0 zu 0 ; 6 zu 200
         .range([0, dimensions.width]) //change
        
      const yScale =  scaleLinear()
         .domain([0, 100])   //todo
         .range([dimensions.height, 30]);  //change

      
      const yAxis = axisLeft(yScale);   
      svg
      .select('.y-axis')
      .style('color', 'grey')
      .style('font-size', '14')
      .call(yAxis);

      //X-Achsen-Beschriftung
      svg.append("text")
      .attr("text-anchor", "end")
      .attr("fill", "grey")
      .attr("x", dimensions.width)
       .attr("y", dimensions.height  + 50)
       .text("Jahr");

     const xAxis = axisBottom(xScale).ticks(4)
     svg
      .select(".x-axis")
      .style('transform', `translateY(${dimensions.height}px)`)
      .style('color', 'grey')
      .style('font-size', '14')
      .style('stroke-width', 1)
      .call(xAxis);
      
      //Grids
      var grids = svg.append("g")
         .attr("class", "grid")
         //.attr('transform', 'translate(50,50)')
         .call(axisLeft(yScale)
            .tickSize(-(dimensions.width))
            .tickFormat("")
            )

      //Y-Achsen-Beschrifzung
      svg.append("text")
    .attr("text-anchor", "end")
    .attr("transform", "rotate(-90)")
    .attr("fill", "grey")
    .attr("y", - 50)
    .attr("x", 0)
    .text("Prozent")

    

 /*     //Tooltip-Circle
      var focus = svg
      .append('g')
      .append('circle')
      .style("fill", "#2770bc")
      .attr('r', 8.5)
      .style("opacity", 0);

      //Tooltip- Values
      var focusText = svg
      .append('g')
      .append('text')
      .style("opacity", 0)
      .attr("text-anchor", "left")
      .attr("alignment-baseline", "middle");

      svg
      .append('rect')
      .style("fill", "none")
      .style("pointer-events", "all")
      .attr('width', dimensions.width)
      .attr('height', dimensions.height)
      .on('mouseover', mouseover)
      .on('mousemove', mousemove)
      .on('mouseout', mouseout); */
      
     

      const myLine = line()
      .x((d)=> (xScale(new Date(Object.keys(d.time)))))
      .y((d)=> (yScale(d[Object.keys(d)]) - dimensions.height))
      .curve(curveCardinal);
     
    svg
      .selectAll("path")
      .data([filtered])
      .join("path")
      .attr("d", (value, index) => myLine(value))
      .attr("fill", "none")
      .attr("stroke", "#2770bc")
      .attr("stroke-width", "5" )
      
   /*   function mouseover() {
         
         focus.style("opacity", 1).transition()
         focusText.style("opacity",1).transition()
       }
       
       function mousemove() {
         // recover coordinate we need
         var x0 = xScale.invert(mouse(this)[0]);
         const bisect = bisector(d => new Date(Object.keys(d.time))).left;
         const idx = bisect(data, x0, 1);
         const selectedData = data[idx];
         console.log( selectedData);
         
         focus
         .attr("cx", xScale(new Date (selectedData.time)))
         .attr("cy", yScale(selectedData.value))
         .transition()

         focusText
            
            .text(selectedData.time  + " : " + selectedData.value)
            .attr("x", xScale(new Date (selectedData.time)))
            .attr("y", yScale(selectedData.value) - 30)
            .transition()
            
       /*  focus
           .attr("cx", xScale(selectedData.x))
           .attr("cy", yScale(selectedData.y))
         focusText
           .html("x:" + selectedData.x + "  -  " + "y:" + selectedData.y)
           .attr("x", selectedData.x)
           .attr("y", yScale(selectedData.y)) 
          
         }
       function mouseout() {
         focus.style("opacity", 0)
         focusText.style("opacity", 0)
       }*/


   }, [data, dimensions, property]);
   return (
         <div ref={wrapperRef}>
            <svg ref={svgRef}>
               <g className='x-axis' />
               <g className="y-axis" />
            </svg>
            </div>
   );
}

export default LineCharts;
