import React, { useRef, useEffect, useState } from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, event, bisector, mouse, ticks, entries, arc, schemeDark2, scaleOrdinal, pie} from 'd3';
import useResizeObserver from "./useReziseObserver";


function PieMobilNutzung({ data, highlight }) {
    const svgRef = useRef();
    const wrapperRef = useRef();
    const dimensions = useResizeObserver(wrapperRef);
    const [selection, setSelection] = useState([0, 1.5]);
 
    useEffect(() => {
        const svg = select(svgRef.current);
        
        if(!dimensions) return;
        //var width = 450;
        //var height = 450;
        //var  margin = 40;

        var w = 300,                        //width
        h = 300,                            //height
        r = 100,                            //radius
        color = scale.category20c();     //builtin range of colors
    
        var pied = pie().value((d) => d.value)(data);
       

      //  var radius = Math.min(width, height) / 2 - margin

                        var vis = svg.select("pie")
                                      //create the SVG element inside the <body>
                        .data([pied])                   //associate our data with the document
                        .attr("width", w)           //set the width and height of our visualization (these will be attributes of the <svg> tag
                        .attr("height", h)
                        .join("g")  
                        .attr("class", "pie")              //make a group to hold our pie chart
                        .attr("transform", "translate(" + r + "," + r + ")")    //move the center of the pie chart from 0, 0 to radius, radius

                    var arc = svg.arc()              //this will create <path> elements for us using arc data
                        .outerRadius(r);

                    var pie = layout.pie()           //this will create arc data for us given a list of values
                        .value(function(d) { return d.value; });    //we must tell it out to access the value of each element in our data array

                    var arcs = vis.selectAll("g.slice")     //this selects all <g> elements with class slice (there aren't any yet)
                        .data(pie)                          //associate the generated pie data (an array of arcs, each having startAngle, endAngle and value properties) 
                        .enter()                            //this will create <g> elements for every "extra" data element that should be associated with a selection. The result is creating a <g> for every object in the data array
                            .append("g")                //create a group to hold each slice (we will have a <path> and a <text> element associated with each slice)
                                .attr("class", "slice");    //allow us to style things in the slices (like text)

                        arcs.append("path")
                                .attr("fill", function(d, i) { return color(i); } ) //set the color for each slice to be chosen from the color function defined above
                                .attr("d", arc);                                    //this creates the actual SVG path using the associated data (pie) with the arc drawing function

                        arcs.append("text")                                     //add a label to each slice
                                .attr("transform", function(d) {                    //set the label's origin to the center of the arc
                                //we have to make sure to set these before calling arc.centroid
                                d.innerRadius = 0;
                                d.outerRadius = r;
                                return "translate(" + arc.centroid(d) + ")";        //this gives us a pair of coordinates like [50, 50]
                            })
                            .attr("text-anchor", "middle")                          //center the text on it's origin
                            .text(function(d, i) { return pied[i].ed; });        //get the label from our original data array
                        
                            
    }, [data, dimensions]);


return (
    <div ref={wrapperRef} >
       <svg ref={svgRef}>
       </svg>
     </div>
 );
 }
 
 export default PieMobilNutzung;
