import React, { useRef, useEffect, useState } from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, event, bisector, mouse, ticks} from 'd3';
import useResizeObserver from "./useReziseObserver";


function NumberGen({ data, property, gen }) {

    const svgRef = useRef();
    const wrapperRef = useRef();
    const dimensions = useResizeObserver(wrapperRef);
    const [selection, setSelection] = useState([0, 1.5]);
 
    useEffect(() => {
        const svg = select(svgRef.current);
     
         if(!dimensions) return;

        var filtered = data.filter(a=>a.geo==property);
       var finalFilter = filtered.filter(b=>b.gen==gen);

       svg
        .selectAll("text")
        .data(finalFilter)
        .join("text")
        .text((s) => (s.time[Object.keys(s.time)[0]]) + "%")
        .attr('x', 0)
        .attr('y', 60)
        .attr("fill", "#2770bc")

        console.log(finalFilter);

}, [data, dimensions, property]);
return (
 <div className="prozentwrap" ref={wrapperRef} >
    <svg className="Prozent" ref={svgRef}>
     
    </svg>
  </div>
);
}

export default NumberGen;
