import React from 'react';
import { Provider } from "react-redux";
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import { createStore } from "redux";
import NavBar from './components/NavBar'
import ECom from './components/pages/ECom';

import './styles/App.css';
import Menschen from './components/pages/Menschen';
import { SET_CURRENT_RED } from "./actions";
import { SET_CURRENT_BLUE } from "./actions";
const initialState = {
  color: "blue",
  index: 1,
  key: "false"
};

function reducer(state = initialState, action) {
  console.log("reducer", state, action);

  switch (action.type) {
    case SET_CURRENT_RED:
      return {
        color: "red",
        index: action.index,
        key: "true"
      };
      case SET_CURRENT_BLUE:
        return {
          color: "blue",
          index: action.index,
          key: "false"
        };  
      default:
        return state;
  }
}

const store = createStore(reducer);

export default function Appl() {
  return (


        <div className="wrapMain">
          <Provider store={store}>
      <Router>
        <NavBar/>
        <Switch>
            <Route path="/" exact component={Menschen}/>
            <Route path="/Menschen"  component={Menschen}></Route>
            <Route path="/ECom" component={ECom}></Route> 
        </Switch>
     </Router>
     </Provider>
     </div>

    );

}
