import React, { useRef, useEffect, useState} from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, ticks, bisector, mouse} from 'd3';
import '../../styles/charts.css'
import useResizeObserver from './useReziseObserver'

function LineCharts({ data, highlight }) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);
   const [selection, setSelection] = useState([0, 1.5]);
  // const [dat, setData] = useState([25, 30, 45, 60, 20, 65, 75]);
   //Wird einmal aufgerufen, wenn DOM-Element gerendert wird, und wenn sich Elemente im Array ver채ndern
   useEffect(() => {

      const svg = select(svgRef.current);
         console.log(data);
      //const test = data.value[0];
     // console.log(test);
         if(!dimensions) return;
      
      //Sortieren nach Jahreszahl
     
      const minDate = new Date (min(data, value => value.time));
      const maxDate = new Date (max(data, value => value.time));
      
     

      console.log(minDate);
      console.log(maxDate);


      const xScale = scaleTime()
          .domain([minDate,maxDate])
        // .domain([0, data.length-1]) //0 zu 0 ; 6 zu 200
         .range([0, dimensions.width]) //change
        // .padding(0.5);
      const yScale =  scaleLinear()
         .domain([0, 100])   //todo
         .range([dimensions.height, 30]);  //change

      const yAxis = axisLeft(yScale);   
      svg
      .select('.y-axis')
      .call(yAxis);

     const xAxis = axisBottom(xScale)
     svg
      .select(".x-axis")
      .style('transform', `translateY(${dimensions.height}px)`)
      .call(xAxis)

      //Exakte Maus-Position
      var bisect = bisector(function(d) { return d.x; }).left;

      //Kreis f체r exakte Position
      var focus = svg
      .append('g')
      .append('circle')
      .style("fill", "none")
      .attr("stroke", "black")
      .attr('r', 8.5)
      .style("opacity", 0)

       // Create the text that travels along the curve of chart
       var focusText = svg
       .append('g')
      .append('text')
      .style("opacity", 0)
      .attr("text-anchor", "left")
      .attr("alignment-baseline", "middle")      

        // Create a rect on top of the svg area: this rectangle recovers mouse position
      svg
      .append('rect')
      .style("fill", "none")
      .style("pointer-events", "all")
      .attr('width', dimensions.width)
      .attr('height', dimensions.height)
      .on('mouseover', mouseover)
      .on('mousemove', dimensions.mousemove)
      .on('mouseout', dimensions.mouseout);
      
      const myLine = line()
      .x((d)=> (xScale(new Date(d.time))))
      .y((d)=> (yScale(d.value) - dimensions.height))
      .curve(curveCardinal);
    svg
      .selectAll("path")
      .data([data])
      .join("path")
      .attr("d", value => myLine(value))
      .attr("fill", "none")
      .attr("stroke", "#2770bc")
      .attr("stroke-width", "5" )

     
      function mouseover() {
         focus.style("opacity", 1)
         focusText.style("opacity",1)
       }
    /*  .on("mouseover", (value, index) => {
         svg
           .selectAll(".tooltip")
           .data([value])
           .join("text")
           .attr("class", "tooltip")
           .attr("stroke", "red")
           .text((d) => d.value)
           console.log((d) => d.value);
       })
       .on("mouseleave", () => svg.select(".tooltip").remove()) */

       
   }, [data, dimensions]);
   return (
         <div ref={wrapperRef}>
            <svg ref={svgRef}>
               <g className='x-axis' />
               <g className="y-axis" />
            </svg>
            </div>
   );
}

export default LineCharts;
