import React, { useRef, useEffect} from "react";
import { select,entries, axisBottom, axisLeft, scaleLinear, scaleBand, ascending} from 'd3';
import useResizeObserver from "./useReziseObserver";
import '../../styles/highlight.css'


function BarHighKeinInternet({data}) {

    const svgRef = useRef();
    const wrapperRef = useRef();
    const dimensions = useResizeObserver(wrapperRef);
 
    useEffect(() => {
 
     const svg = select(svgRef.current);
     var filterme; 
     if(!dimensions) return;
        
        for(var i = 0; i < data.length; i ++) {
            filterme = data[i].gründe; 
             }
             var vales = entries(filterme);
             vales = vales.slice().sort((a, b) => ascending(a.value, b.value))
             console.log(vales)

        const yScale = scaleLinear()
        .domain([0, 50])
       .range([0, dimensions.width])
       
        const xScale =  scaleBand()
       .domain(vales.map((value, index) => value.key))
       .range([0, dimensions.height])
       .padding(0.2)
    

       const xAxis = axisBottom(xScale).ticks(5).tickSize(0).tickPadding(10);
       svg
       .style('font-size', '11')
       .style('color', 'white')
        .select(".x-axis")
        .call(xAxis);
    
        svg.select(".domain").remove()

        svg.select(".x-axis")
        .attr('transform', 'translate(0,' + dimensions.height + ')')

     const yAxis = axisLeft(yScale)  
          svg
            .select(".y-axis")
            .remove()

            svg
            .selectAll(".bar")
            .data(vales)
            .join("rect")
            .attr("class", "bar")
            .attr("fill", "#c8a3c8")
            .attr("x", (d)=> (xScale(d.key)))
            .attr("y", (d)=> (yScale(d.value)))
            .attr("height", (d)=> yScale(0) - yScale(d.value))
            .attr("width", xScale.bandwidth )
           .on("mouseenter", (value, index) => {
              svg
                .selectAll(".tooltip")
                .data([value])
                .join(enter => enter.append("text"))
                .attr("class", "tooltip")
                .text((value.value) + "%")
                .attr("x", xScale(value.key) + xScale.bandwidth()/ 6)
                .attr("y", yScale(value.value) + 15)
                .attr("opacity", 1)
                .transition()
                .attr("fill", "white")
              })
              
              .on("mouseleave", () => 
                svg.select(".tooltip").remove()
                .transition()
     )
  
}, [data, dimensions]);
return (
 <div className="chartHighKeinInternet" ref={wrapperRef}>
    <svg ref={svgRef}>
      <g className="x-axis" />
      <g className="y-axis" />
    </svg>
  </div>
);
}

export default BarHighKeinInternet;
