import React, { useRef, useEffect, useState } from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleOrdinal, scaleLinear, scaleBand, csv, min, max, event, entries,bisector, mouse, curveLinear, area, nest, extent, timeParse} from 'd3';
import useResizeObserver from "./useReziseObserver";

function AreaIKTBeschäftigung({property }) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);
   const [selection, setSelection] = useState([0, 1.5]);
  // const [dat, setData] = useState([25, 30, 45, 60, 20, 65, 75]);
   //Wird einmal aufgerufen, wenn DOM-Element gerendert wird, und wenn sich Elemente im Array verändern
   useEffect(() => {

      const svg = select(svgRef.current);

      var filtered = data.filter(a=>a.geo==property);

      //const test = data.value[0];
     // console.log(test);
         if(!dimensions) return;
      
      //Sortieren nach Jahreszahl

     //Read the data
csv("https://raw.githubusercontent.com/holtzy/data_to_viz/master/Example_dataset/5_OneCatSevNumOrdered.csv", function(data) {

  // group the data: I want to draw one line per group
  var sumstat = nest() // nest function allows to group the calculation per level of a factor
    .key(function(d) { return d.name;})
    .entries(data);

  // Add X axis --> it is a date format
  var x = scaleLinear()
    .domain(extent(data, function(d) { return d.year; }))
    .range([ 0, dimensions.width ]);
  svg.append("g")
    .attr("transform", "translate(0," + dimensions.height + ")")
    .call(axisBottom(x).ticks(5));

  // Add Y axis
  var y = d3.scaleLinear()
    .domain([0, max(data, function(d) { return +d.n; })])
    .range([ height, 0 ]);
  svg.append("g")
    .call(axisLeft(y));

  // color palette
  var res = sumstat.map(function(d){ return d.key }) // list of group names
  var color = scaleOrdinal()
    .domain(res)
    .range(['#e41a1c','#377eb8','#4daf4a','#984ea3','#ff7f00','#ffff33','#a65628','#f781bf','#999999'])

  // Draw the line
  svg.selectAll(".line")
      .data(sumstat)
      .enter()
      .append("path")
        .attr("fill", "none")
        .attr("stroke", function(d){ return color(d.key) })
        .attr("stroke-width", 1.5)
        .attr("d", function(d){
          return d3.line()
            .x(function(d) { return x(d.year); })
            .y(function(d) { return y(+d.n); })
            (d.values)
        })

})


   }, [data, dimensions, property]);
    return (
     <div  className="IKTWarapperRef" ref={wrapperRef} >
        <svg ref={svgRef}>
          <g className="x-axis" />
          <g className="y-axis" />
        </svg>
      </div>
    );
    }
    
    export default AreaIKTBeschäftigung;
