import React, { useRef, useEffect, useState } from "react";
import { select, axisBottom, axisLeft, scaleTime, scaleLinear, min, max, entries,bisector, mouse, curveLinear, area, } from 'd3';
import useResizeObserver from "./useReziseObserver";

function AreaIKTBeschäftigung({ data, property }) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);
 
   useEffect(() => {

      const svg = select(svgRef.current);
     var filterme;
     var filterMann;
      if(!dimensions) return;

      //Daten filtern
      var filtered = data.filter(a=>a.geo==property);
      var filtered = data.filter(a=>a.geo==property);
     
    for(var i = 0; i < filtered.length; i ++) {
       var filterme = filtered[i].Frauen;
       var filterMann = filtered[i].Männer;  
    }

    var valesFrau = entries(filterme);
    var valesMann = entries(filterMann);

    var objectsIKT = {gen: valesFrau, gens: valesMann};

    const minDate = new Date (min(objectsIKT.gen, value => value.key));
    const maxDate = new Date (max(objectsIKT.gen, value => value.key));     

    console.log(minDate);
    console.log(maxDate);


      const xScale = scaleTime()
          .domain([minDate,maxDate])
         .range([0, dimensions.width])
        
      const yScale =  scaleLinear()
         .domain([0, 100])
         .range([dimensions.height, 30]);

      
         const yAxis = axisLeft(yScale).tickSize(0);   
         svg
         .select('.y-axis')
         .style('color', 'grey')
         .style('font-size', '14')
         .call(yAxis);
   
        const xAxis = axisBottom(xScale)
        svg
         .select(".x-axis")
         .join(".x-axis")
         .style('transform', `translateY(${dimensions.height}px)`)
         .style('color', 'grey')
         .style('font-size', '14')
         .style('stroke-width', 1)
         .call(xAxis);
   
         //Grids
         var grids = svg.select("g").append("g")
            .join("g")
            .attr("class", "grid")
            .attr('transform', 'translate(0,' + -dimensions.height + ')')
            .call(axisLeft(yScale)
               .tickSize(-dimensions.width)
               .tickFormat("")
               )
         
         //X-Achsen-Beschriftung
         svg.select("text").join("text")
         .attr("text-anchor", "end")
         .attr("fill", "grey")
         .attr("x", dimensions.width)
          .attr("y", + dimensions.height/ 8)
          .attr("font-weight", "bold")
          .text("Jahr");      
         //Y-Achsen-Beschrifzung
         svg.append("text")
         .attr("text-anchor", "end")
         .attr("transform", "rotate(-90)")
         .attr("fill", "grey")
         .attr("y", - 50)
         .attr("x", 0)
         .text("Prozent")

         const tooltip = select('#tooltip');
         const tooltipLine = svg.append('line');
            //  .on('mousemove', drawTooltip)
             // .on('mouseout', removeTooltip);
             var focusFrau = svg
             .append('g')
             .append('circle')
             .style("fill", "none")
             .attr('r', 8.5)
             .style("opacity", 0)
             .attr("stroke", "#d043ee")

             var focusMann = svg
             .append('g')
             .append('circle')
             .style("fill", "none")
             .attr('r', 8.5)
             .style("opacity", 0)
             .attr("stroke", "#6810d4")
             .attr("stroke-width", 3)

              svg
              .append('rect')
              .style("fill", "none")
              .style("pointer-events", "all")
              .attr('width', dimensions.width)
              .attr('height', dimensions.height)
              .on('mouseover', mouseover)
              .on('mousemove', mousemove)
              .on('mouseout', mouseout);
              
               /* tooltipLine.attr('stroke', 'black')
                .data([valesFrau])
                .attr('x1', (d)=> (xScale(new Date(d.key))))
                .attr('x2', (d)=> (xScale(new Date(d.key))))
                .attr('y1', 0)
                .attr('y2', dimensions.height); */
               // Tooltip-Circle
                var focus = tooltipLine
                .attr('stroke', 'black')
                .data([valesFrau])
                .attr('x1', (d)=> (xScale(new Date(d.key))))
                .attr('x2', (d)=> (xScale(new Date(d.key))))
                .attr('y1', 0)
                .attr('y2', dimensions.height);

                function mousemove() {
                  var x0 = xScale.invert(mouse(this)[0]);
                  const bisect = bisector(d => new Date(d.key)).left;
                  const idx = bisect(valesFrau, x0, 1);
                  const selectedDataFrau = valesFrau[idx];
                  console.log( selectedDataFrau);

                  const idxM = bisect(valesMann, x0, 1);
                  const selectedDataMann = valesMann[idxM];
                  console.log( selectedDataMann);
                  
                /*  tooltipLine
                  .attr("x", xScale(new Date (selectedData.key)))
                  .attr("y", yScale(selectedData.value) - 30) */
                  focus
                  .transition()
                  .duration(80)
                  .attr("x1", xScale(new Date (selectedDataFrau.key)))
                  .attr("x2", xScale(new Date (selectedDataFrau.key)))
                  .attr("y1", yScale(selectedDataFrau.value) - dimensions.height )
                  .attr("y2",  dimensions.height)
                  
                  focusMann
                  .transition()
                  .duration(80)
                  .attr("cx", xScale(new Date(selectedDataMann.key)))
                  .attr("cy", yScale(selectedDataMann.value))

                  focusFrau
                  .transition()
                  .duration(80)
                  .attr("cx", xScale(new Date(selectedDataFrau.key)))
                  .attr("cy", yScale(selectedDataFrau.value))
         
               /*   focusText
                  .transition()
                  .duration(80)
                     .text(selectedData.key  + " : " + selectedData.value + "%")
                     .attr("x", xScale(new Date (selectedData.key)))
                     .attr("y", yScale(selectedData.value) - 30) */
                   
                  }
                function mouseout() {
                  focus.style("opacity", 0)
                  focusMann.style("opacity", 0)
                  focusFrau.style("opacity", 0)
                 // focusText.style("opacity", 0)
                }
                function mouseover() {
                  focusMann.style("opacity", 1)
                  focusFrau.style("opacity", 1)
                  focus.style("opacity", 1)
                 // focusText.style("opacity", 0)
                }
     /* //Tooltip-Circle
      var focus = svg
      .append('g')
      .append('circle')
      .style("fill", "#2770bc")
      .attr('r', 8.5)
      .style("opacity", 0);

      //Tooltip- Values
      var focusText = svg
      .append('g')
      .append('text')
      .style("opacity", 0)
      .attr("text-anchor", "left")
      .attr("alignment-baseline", "middle");

       */

      const curve = curveLinear;

      const myArea = area()
      .x((d)=> (xScale(new Date(d.key))))
      .y0(dimensions.height)
      .y1((d)=> (yScale(d.value)))
      .curve(curve)

        svg
         .selectAll(".newArea")
         .data([valesMann])
         .join("path")
         .attr("class", "newArea")
         .attr("d", (value, index) => myArea(value))
         .transition()
         .attr("fill", "#9f77d0")
         .attr("stroke", "#6810d4")
         
        
         svg
         .selectAll(".newerArea")
         .data([valesFrau])
         .join("path")
         .attr("class", "newerArea")
         .attr("d", (value, index) => myArea(value))
         .transition()
         .attr("fill", "none")
         .attr("fill", "#d781d7")
        .attr("stroke", "#d043ee")
         
    /*  var path1 = svg
      .append("path")
      .data([valesMann])
      .join("path")
      .attr("class", "line1")
      .exit().remove();

      var path2 = svg  
      .append("path")
      .data([valesFrau]) 
      .join("path")
      .attr("class", "line2")
      .exit().remove();
      tick();

      function tick() {
        svg.selectAll(".line1")
        .attr("d", (value, index) => myArea(value))
        .attr("fill", "#9f77d0")
        .attr("stroke", "#6810d4")
        .attr("stroke-width", "1.2" )
        

        svg.selectAll(".line2")
        .attr("d", (b, index) => myArea(b))
        .attr("fill", "#d781d7")
        .attr("stroke", "#d043ee")
      .attr("stroke-width", "1.2" )
      

      }

/*    svg
      .selectAll("path")
      .join("area2")
      .data([valesMann])
      .attr("d", (value, index) => myLine(value))
      .attr("fill", "#9f77d0")
      .attr("stroke", "#6810d4")
      .attr("stroke-width", "1.2" )
     
     
     var path = svg.append("path");
      path
      .data([valesFrau])
      .join("line")
      .attr("d", (a, index) => myLine2(a))
      .attr("fill", "#d781d7")
      .attr("stroke", "#d043ee")
      .attr("stroke-width", "1.2" )*/
  
    /*  function mouseover() {
         
         focus.style("opacity", 1).transition()
         focusText.style("opacity",1).transition()
       }
       
       function mousemove() {
         var x0 = xScale.invert(mouse(this)[0]);
         const bisect = bisector(d => new Date(d.key)).left;
         const idx = bisect(valesFrau, x0, 1);
         const selectedData = valesFrau[idx];
         console.log( selectedData);
         
         focus
         .transition()
         .duration(80)
         .attr("cx", xScale(new Date (selectedData.key)))
         .attr("cy", yScale(selectedData.value))
         

         focusText
         .transition()
         .duration(80)
            .text(selectedData.key  + " : " + selectedData.value + "%")
            .attr("x", xScale(new Date (selectedData.key)))
            .attr("y", yScale(selectedData.value) - 30)
          
         }
       function mouseout() {
         focus.style("opacity", 0)
         focusText.style("opacity", 0)
       }

*/
   }, [data, dimensions, property]);
    return (
     <div  className="IKTWarapperRef" ref={wrapperRef} >
        <svg ref={svgRef}>
          <g className="x-axis" />
          <g className="y-axis" />
        </svg>
      </div>
    );
    }
    
    export default AreaIKTBeschäftigung;
