      svg.select('.y-axis')
         .style('transform', `translateX(${dimensions.width}px -100)`)
         .call(yAxis);

     const xAxis = axisBottom(xScale)
     svg
      .select(".x-axis")
      .style('transform', `translateY(${dimensions.height}px)`)
      .call(xAxis)

