import React, { useRef, useEffect, useState} from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleLinear, scaleBand } from 'd3';
import '../../styles/charts.css'

const useResizeObserver = ref => { //Beobachtet die Größe des Browsers
   const [dimensions, setDimensions] = useState(null);
   useEffect(() => {
     const observeTarget = ref.current;
     const resizeObserver = new ResizeObserver(entries => {
       entries.forEach(entry => {
         setDimensions(entry.contentRect);
       });
     });
     resizeObserver.observe(observeTarget);
     return () => {
       resizeObserver.unobserve(observeTarget);
     };
   }, [ref]);
   return dimensions;
 };


function LineCharts({ data }) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);
   //Wird einmal aufgerufen, wenn DOM-Element gerendert wird, und wenn sich Elemente im Array verändern
   useEffect(() => {
      const svg = select(svgRef.current);
         console.log(dimensions);

         if(!dimensions) return;
      
      const xScale = scaleBand()
         .domain(data.map((value, index) => index)) //0 zu 0 ; 6 zu 200
         .range([0, dimensions.width]) //change
         .padding(0.5);

      const yScale =  scaleLinear()
         .domain([10, 100])   //todo
         .range([dimensions.height, 0]);  //change

      
         const colorScale =  scaleLinear()
         .domain([75, 100, 100])
         .range(["green", "orange", "red"])
         .clamp(true);  

      const yAxis = axisLeft(yScale);   
      svg.select('.y-axis')
         .style('transform', `translateX(${dimensions.width}px)`)
         .call(yAxis);

     const xAxis = axisBottom(xScale).ticks(data.length);
     svg
      .select(".x-axis")
      .style('transform', `translateY(${dimensions.height}px)`)
      .call(xAxis);
     /**  svg
      .selectAll('circle')
      .data(data)
      .join('circle')
      .attr("r", value => value)
      .attr('cx', value => value * 2)
      .attr("cy", value => value * 2)
      .attr("stroke", 'red');*/
    
   /* .selectAll(".bar")
    .data(data)
    .join("rect")
    .attr("class", "bar")
    .style("transform", "scale(1, -1") //Animation: Auf den KOpf stellen!
    .attr("x", (value, index) => xScale(index))
    .attr("y", -dimensions.height) //Animation: nach unten, weil die Bars gedreht wurden
    .attr("width", xScale.bandwidth())
    .on("mouseenter", (value, index) => {
       svg //Tooltip
       .selectAll(".tooltip")
       .data([value])
       .join(enter => enter.append("text").attr("y", yScale(value) -4))
       .attr("class", "tooltip")
       .text(value)
       .attr("x", xScale(index) + xScale.bandwidth() / 2 )
       
       .attr("text-anchor", "middle")
       .transition()
       .attr("y", yScale(value) - 8)
       .attr("opacity", 1);
    })
    .on("mouseleave", () => 
    svg
    .select(".tooltip").remove())
    .transition()
    .attr("fill", colorScale)
    .attr("height", value => dimensions.height - yScale(value))*/
   }, [data, dimensions]);
   return (
         <div ref={wrapperRef}>
            <svg ref={svgRef}>
               <g className='x-axis' />
               <g className="y-axis" />
            </svg>
            </div>
   );
}

export default LineCharts;
