import React, { useRef, useEffect, useState } from "react";
import { select, scaleOrdinal, pie, arc, } from 'd3';
import useResizeObserver from "./useReziseObserver";
import '../../styles/highlight.css'


function BarHighInternet({ data, number}) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);

   useEffect(() => {

    const svg = select(svgRef.current);
  
    if(!dimensions) return;
    console.log(data);

       var width = dimensions.width  ;
       var outerRadius = width/4;
      

       var pied = pie().value((d) => d.time[Object.keys(d.time)[number]])(data);

 
         var color = scaleOrdinal()
         .domain(pied)
         .range(["#a8a7a8","#d043ee","#7223e7"])

         var arcd = arc()
         .innerRadius(90)         // This is the size of the donut hole
         .outerRadius(outerRadius/1.5)


        var g = svg.selectAll(".newPie")
        .data(pied)
        .join("path")
        .attr("class", "newPie")
        .attr("d", arcd)
        .attr("fill", function(d) { return color(d.data.häufig); })
        .attr("stroke", "#203040")
         .style("stroke-width", "6px")
         .style("opacity", 1)
         
         g.transition()
         .duration(80);
         
         svg.selectAll(".newText")
         .data(pied)
         .join("text")
         .attr("class", "newText")
        .attr("transform", function(d) {
          var _d = arcd.centroid(d);
       //   _d[0] -= 2.0;	
       //   _d[1] += 0.3;
          return "translate(" + _d + ")";
        })
        .attr("fill", "white")
        .attr("font-size", "12px")
        .attr("text-anchor", "middle")
        .text(function(d) { return d.data.häufig; });


        g.on("mouseover", function(d) {
          svg.selectAll(".tooltip")
          .attr("transform", function(d) {
           return "translate(" + arcd.centroid(d) + ")"})
             .data([data])
             .join(enter => enter.append("text"))
             .attr("class", "tooltip")
            .text(d.value + "%")
            .attr("x", -12 + "%")
            .attr("y", 3 + "%")
            .attr("fill", "white")
            .attr("font-size", "30")
          })
          .on("mouseout", function(d) {
            svg.select(".tooltip").remove()
  })

   
}, [data, dimensions]);
return (
 <div className="chartHigh" ref={wrapperRef}>
    <svg ref={svgRef}>
      <g className="x-axis" />
      <g className="y-axis" />
    </svg>
  </div>
);
}

export default BarHighInternet;
