import React, { useRef, useEffect, useState } from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, event, bisector, mouse, ticks} from 'd3';
import useResizeObserver from "./useReziseObserver";


function BarMobilNutzung({ data, highlight }) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);
   const [selection, setSelection] = useState([0, 1.5]);

   useEffect(() => {

    const svg = select(svgRef.current);
     
         if(!dimensions) return;
      
         const xScale = scaleLinear()
         .domain([0, 100])
        .range([0, dimensions.width]) //change
        
       
         const yScale =  scaleBand()
        .domain(data.map((value, index) => value.age))   //todo
        .range([0, dimensions.height]);  //change

        const xAxis = axisBottom(xScale);
            svg
          .select(".x-axis")
         // .attr("transform", `translateY(${dimensions.height}px)rotate(-45)`)
          .call(xAxis);

          const yAxis = axisLeft(yScale).ticks(data.length);
          svg
            .select(".y-axis")
            .call(yAxis);

            svg
          .selectAll(".bar")
          .data(data)
          .join("rect")
          .attr("class", "bar")
          .attr("fill", "grey")
          .attr('x', xScale(0))
          .attr('y', (s) => xScale(s.age))
          .attr('width', (s) => xScale(s.value))
        .attr('height', yScale.bandwidth())
  }, [data, dimensions]);
  return (
   <div ref={wrapperRef} >
      <svg ref={svgRef}>
        <g className="x-axis" />
        <g className="y-axis" />
      </svg>
    </div>
);
}

export default BarMobilNutzung;
