import React, { useRef, useEffect, useState } from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, event, bisector, mouse, ticks, entries, arc, schemeSet2, scaleOrdinal} from 'd3';
import useResizeObserver from "./useReziseObserver";


function PieMobilNutzung({ data, highlight }) {
    const svgRef = useRef();
    const wrapperRef = useRef();
    const dimensions = useResizeObserver(wrapperRef);
    const [selection, setSelection] = useState([0, 1.5]);
 
    useEffect(() => {
        const svg = select(svgRef.current);
        var radius = Math.min(dimensions.width, dimensions.height) / 2;
        console.log(dimensions.width);
        var color = scaleOrdinal()
            .domain(data)
            .range(["#2C93E8","#838690","#F56C4E"]);

            var pie = pie()
	        .value(function(d) { return d.values; })(data);

            var arc = arc()
                .outerRadius(radius - 10)
                .innerRadius(0);

            var labelArc = arc()
                .outerRadius(radius - 40)
                .innerRadius(radius - 40);

            svg = select("pie")
                .join("pied")
                .attr("class", "pie")
                .attr("width", dimensions.width)
                .attr("height", dimensions.height)
                .append("g")
                .attr("transform", "translate(" + dimensions.width/2 + "," + dimensions.height/2 +")"); 

    }, [data, dimensions]);


return (
    <div ref={wrapperRef} >
       <svg ref={svgRef}>
       </svg>
     </div>
 );
 }
 
 export default PieMobilNutzung;
