import React, { useRef, useEffect, useState } from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, event, bisector, mouse, ticks, entries, arc, schemeDark2, scaleOrdinal, pie} from 'd3';
import useResizeObserver from "./useReziseObserver";


function PieMobilNutzung({ data, highlight }) {
    const svgRef = useRef();
    const wrapperRef = useRef();
    const dimensions = useResizeObserver(wrapperRef);
    const [selection, setSelection] = useState([0, 1.5]);
 
    useEffect(() => {
        const svg = select(svgRef.current);
        
        if(!dimensions) return;
        //var width = 450;
        //var height = 450;
        //var  margin = 40;

        var colors = scaleOrdinal(schemeDark2);


        var pied = pie().value((d) => d.value)(data);
       

      //  var radius = Math.min(width, height) / 2 - margin

        var segments = arc()
                        .innerRadius(0)
                        .outerRadius(200)
                        .padAngle(.05)
                        .padRadius(50);
        
        var sections = svg.append("g")
                        .attr("transform", "transform(250, 250)")
                        .selectAll("path")
                        .data(pied);

                        sections.enter()
                        .append("path")
                        .attr("d", segments).attr("fill", (d) => colors(d.value));
        
        var content = select("g")
                        .selectAll("text")
                        .data(pied);

                        content.enter()
                        .join("text").each(function(d){  var center = segments.centroid(d);
                        select(this).attr("x", center[0].attr("y", center[1]
                        .text(d.pied.value)));
                        })
        
    }, [data, dimensions]);


return (
    <div ref={wrapperRef} >
       <svg ref={svgRef}>
       </svg>
     </div>
 );
 }
 
 export default PieMobilNutzung;
