import React, { useRef, useEffect, useState} from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, ticks} from 'd3';
import { timeParse } from "d3-time-format";
import '../../styles/charts.css'
import useResizeObserver from './useReziseObserver'

function LineCharts({ data, highlight }) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);
   
  // const [dat, setData] = useState([25, 30, 45, 60, 20, 65, 75]);
   //Wird einmal aufgerufen, wenn DOM-Element gerendert wird, und wenn sich Elemente im Array verändern
   useEffect(() => {

      const svg = select(svgRef.current);
         console.log(data);
      //const test = data.value[0];
     // console.log(test);
         if(!dimensions) return;
      
      //Sortieren nach Jahreszahl
     
      const minDate = new Date (min(data, value => value.time));
      const maxDate = new Date (max(data, value => value.time));
      
     

      console.log(minDate);
      console.log(maxDate);


      const xScale = scaleTime()
          .domain([minDate,maxDate])
        // .domain([0, data.length-1]) //0 zu 0 ; 6 zu 200
         .range([0, dimensions.width]) //change
        // .padding(0.5);
      const yScale =  scaleLinear()
         .domain([0, 100])   //todo
         .range([dimensions.height, 0]);  //change

      const yAxis = axisLeft(yScale);   
      svg.select('.y-axis')
        
         .call(yAxis);

     const xAxis = axisBottom(xScale)
     svg
      .select(".x-axis")
      .style('transform', `translateY(${dimensions.height}px)`)
      .call(xAxis)

      const myLine = line()
      .x((d)=> (xScale(new Date(d.time))))
      .y((d)=> (yScale(d.value) - dimensions.height))
      .curve(curveCardinal);
    svg
      .selectAll("path")
      .data([data])
      .join("path")
      .attr("d", value => myLine(value))
      .attr("fill", "none")
      .attr("stroke", "#2770bc")
      .attr("stroke-width", "5" )
      .on('mouseover', function (d, i) {
         d3.select(this).transition()
              .duration('50')
              .attr('opacity', '.85');
         div.transition()
              .duration(50)
              .style("opacity", 1);
         let num = (Math.round((d.value / d.data.all) * 100)).toString() + '%';
         div.html(num)
              .style("left", (d3.event.pageX + 10) + "px")
              .style("top", (d3.event.pageY - 15) + "px");
    })
    .on('mouseout', function (d, i) {
         d3.select(this).transition()
              .duration('50')
              .attr('opacity', '1');
         div.transition()
              .duration('50')
              .style("opacity", 0);
    });
   }, [data, dimensions]);
   return (
         <div ref={wrapperRef}>
            <svg ref={svgRef}>
               <g className='x-axis' />
               <g className="y-axis" />
            </svg>
            </div>
   );
}

export default LineCharts;
