import React from "react";
import * as d3 from "d3";
import { connect } from "react-redux";
import { setCurrentBarRed } from "../../actions";
import { setCurrentBarBlue } from "../../actions";
import data from "../../data.json";

// Add function:
function mapStateToProps(state) {
  return {
    color: state.color,
   // index: state.index,
  };
}

class BarChart extends React.Component {
  
  componentDidMount() {
    this.drawBarChart(data);
  }
  drawBarChart(data)  {
    const SVG_HEIGHT = 500;
    const SVG_WIDTH = 1000;
    const MARGIN = { TOP: 20, BOTTOM: 50, LEFT: 50, RIGHT: 15 };

    const GRAPH_WIDTH = SVG_WIDTH - MARGIN.LEFT - MARGIN.RIGHT;
    const GRAPH_HEIGHT = SVG_HEIGHT - MARGIN.TOP - MARGIN.BOTTOM;

    const SCALES = {
      X_SCALE: d3
        .scaleBand()
        .domain(data.map(d => d.region))
        .range([0, GRAPH_WIDTH])
        .paddingInner(0.2)
        .paddingOuter(0.2),
      Y_SCALE: d3
        .scaleLinear()
        .domain([
          0,
          d3.max(data, function(d) {
            return d.profit;
          })
        ])
        .range([GRAPH_HEIGHT, 0])
    };

    let xAxisCall = d3.axisBottom(SCALES.X_SCALE);
    let yAxisCall = d3.axisLeft(SCALES.Y_SCALE);
    const that = this;

    // Create the SVG graph
    this.svgGroup = d3
      .select(this.refs.canvas)
      .append("svg")
      .attr("width", GRAPH_WIDTH + MARGIN.LEFT + MARGIN.RIGHT)
      .attr("height", GRAPH_HEIGHT + MARGIN.TOP + MARGIN.BOTTOM)
      .append("g")
      .attr("transform", "translate(" + MARGIN.LEFT + ", " + MARGIN.TOP + ")");

    // Create The SVG Axis
    this.svgGroup
      .append("g")
      .attr("class", "x axis")
      .attr("transform", "translate(0, " + GRAPH_HEIGHT + ")")
      .call(xAxisCall)
      .selectAll("text")
      .attr("y", "10")
      .attr("x", "10")
      .attr("text-anchor", "end");
      this.svgGroup
      .append("g")
      .attr("class", "y axis")
      .call(yAxisCall);

    // Create the SVG rects
    this.svgGroup
      
      .append("path")
      .data(data)
      .enter()
      .attr("d", this.line().
        x(function(d) {
          return SCALES.X_SCALE(d.region)
        }).
        y(function(d) {
          return SCALES.Y_SCALE(d.profit)
        })
      )
      .attr("fill", "none")
      .attr("stroke", this.props.color)
      .attr("stroke-width", 1.5)
      .attr("fill-opacity", 0.6)
     /**  .on("click", function(d, i) {
        that.props.dispatch(setCurrentBarRed(i));
        // that.handleClick(this);
      });*/
  }

  componentDidUpdate() {
    this.svgGroup
      //.selectAll("path")
      //.filter((d, i) => i === this.props.index)
      .attr("fill", this.props.color); 
    }
  



  render()
   { return <div ref="canvas"></div> }
}
export default connect(mapStateToProps)(BarChart);


