import React, { useRef, useEffect, useState } from "react";
import { select, min, max, entries, axisBottom, axisLeft, scaleTime, scaleOrdinal, scaleLinear, pie, arc, scaleBand} from 'd3';
import useResizeObserver from "./useReziseObserver";
import '../../styles/highlight.css'


function BarHighInternet({ data}) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);

   useEffect(() => {

    const svg = select(svgRef.current);
  
    if(!dimensions) return;
    console.log(data);

   // for(var i = 0; i < data.length; i ++) {
     //   var filterme = data[i].time; 
       //  }

       var width = dimensions.width /1.5 ;
       var outerRadius = width/2;
       var innerRadius = 0;

       var pied = pie().value((d) => d.time[Object.keys(d.time)[0]])(data);

 
         var color = scaleOrdinal()
         .domain(pied)
         .range(["#d6d6d6","#da26da","#6811d4"])

         var arcd = arc()
         .innerRadius(90)         // This is the size of the donut hole
         .outerRadius(outerRadius/1.5)


         var g = svg.selectAll(".arc")
        .data(pied)
        .enter().append("g")
        .attr("class", "arc");

        g.append("path")
        .attr("d", arcd)
        .attr("fill", function(d) { return color(d.data.häufig); })
        .attr("stroke", "white")
         .style("stroke-width", "2px")
         .style("opacity", 0.7)

        g.append("text")
        .attr("transform", function(d) {
          var _d = arcd.centroid(d);
          _d[0] *= 1.4;	
          _d[1] *= 1.9;
          return "translate(" + _d + ")";
        })
        .attr("dy", ".30em")
        .attr("fill", "white")
        .text(function(d) { return d.data.häufig; });

        g.on("mouseover", function(d) {
          g.selectAll(".tooltip")
          .attr("transform", function(d) {
           return "translate(" + arcd.centroid(d) + ")"})
             .data([data])
             .join(enter => enter.append("text"))
             .attr("class", "tooltip")
            .text(d.value + "%")
            .attr("x", -19)
            .attr("y", 2 + "%")
            .attr("fill", "white")
            .attr("font-size", "20")
          })
       /*  svg
         .selectAll('.piedClass')
         .data(pied)
         .join("path")
         .attr("class", "piedClass")
         .attr('d', arcd )
         .attr('fill', function(d){ return(color(d.data.häufig)) })
         .attr("stroke", "white")
         .style("stroke-width", "2px")
         .style("opacity", 0.7)

         svg
         .selectAll('.label')
         .join("text")
         .attr("class", "label")
         .attr("transform", function(d) {
          return "translate(" + arcd.centroid(d) + ")"})
          .attr("text-anchor", "middle")
          .text(function(d) {return d.data.häufig})
          .attr("fill", "white")
        */

   
}, [data, dimensions]);
return (
 <div className="chartHigh" ref={wrapperRef}>
    <svg ref={svgRef}>
      <g className="x-axis" />
      <g className="y-axis" />
    </svg>
  </div>
);
}

export default BarHighInternet;
