import React, { useRef, useEffect, useState } from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, event, bisector, mouse, ticks} from 'd3';
import useResizeObserver from "./useReziseObserver";


function BarNutzungsGründe({ data, highlight }) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);
   const [selection, setSelection] = useState([0, 1.5]);

   useEffect(() => {

    const svg = select(svgRef.current);
     
    if(!dimensions) return;
 
    const xScale = scaleLinear()
    .domain([0, 100])
   .range([0, dimensions.width]) //change
   
  
    const yScale =  scaleBand()
   .domain(data.map((value, index) => value.GEO))   //todo
   .range([0, dimensions.height ])  //change
   .padding(0.2)

   const xAxis = axisBottom(xScale).ticks(4);
       svg
     .select(".x-axis")
     .call(xAxis);
    
     const yAxis = axisLeft(yScale)
          .tickPadding(20)
          .tickSizeOuter(0)
          .tickSizeInner(0);
          svg
            .select(".y-axis")
            .style('font-size', '10')
            .style('color', 'grey')
            .call(yAxis);

            svg
          .selectAll(".bar")
          .data(data)
          .join("rect")
          .attr("class", "bar")
          .attr("fill", "grey")
          .style("margin-top", "10px")
          .attr('x', xScale(0))
          .attr('y', (s) => yScale(s.GEO))
          .attr('width', (s) => xScale(s.Jahr2006))
          .attr('height', yScale.bandwidth)

}, [data, dimensions]);
return (
 <div classname="chartWrap" ref={wrapperRef} style={{height: 500, width: 600}} >
    <svg ref={svgRef}>
      <g className="x-axis" />
      <g className="y-axis" />
    </svg>
  </div>
);
}

export default BarNutzungsGründe;
