import React, { useRef, useEffect, useState } from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, event, entries,bisector, mouse, curveLinear, area, nest, extent, timeParse} from 'd3';
import useResizeObserver from "./useReziseObserver";

function AreaIKTBeschäftigung({ data, property }) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);
   const [selection, setSelection] = useState([0, 1.5]);
  // const [dat, setData] = useState([25, 30, 45, 60, 20, 65, 75]);
   //Wird einmal aufgerufen, wenn DOM-Element gerendert wird, und wenn sich Elemente im Array verändern
   useEffect(() => {

      const svg = select(svgRef.current);

      var filtered = data.filter(a=>a.geo==property);

      //const test = data.value[0];
     // console.log(test);
         if(!dimensions) return;
      
      //Sortieren nach Jahreszahl

    
      console.log(filtered);
    //  console.log(filterme);
     // var valesMann = entries(filterHim);
      
     // delete filterme[0];


     // var objectIKT = valesFrau.concat(valesMann);

   // var sumstat = nest() // nest function allows to group the calculation per level of a factor
   // .key(function(d) { return d.Frauen;})
   // .key(function(d) { return d.Männer;})
   // .entries(objectIKT);
   //     for(var i = 0; i < filtered.length; i ++) {
   //       vales = vales[i].value;           
    //    }
console.log();

      const minDate = min();
     const maxDate = max(); 

      console.log(minDate);
      console.log(maxDate);

      const xScale = scaleTime()
          .domain([])
          
        // .domain([0, data.length-1]) //0 zu 0 ; 6 zu 200
         .range([0, dimensions.width]) //change
        
      const yScale =  scaleLinear()
         .domain([0, 100])   //todo
         .range([dimensions.height, 30]);  //change 

      
      const yAxis = axisLeft(yScale);   
      svg
      .select('.y-axis')
      .style('color', 'grey')
      .style('font-size', '14')
      .call(yAxis);

      //X-Achsen-Beschriftung
      svg.append("text")
      .attr("text-anchor", "end")
      .attr("fill", "grey")
      .attr("x", dimensions.width)
       .attr("y", dimensions.height  + 50)
       .text("Jahr");

     const xAxis = axisBottom(xScale)
     svg
      .select(".x-axis")
      .style('transform', `translateY(${dimensions.height}px)`)
      .style('color', 'grey')
      .style('font-size', '14')
      .style('stroke-width', 1)
      .call(xAxis, grids);
      
      //Grids
      var grids = svg.append("g")
         .attr("class", "grid")
         //.attr('transform', 'translate(50,50)')
         .call(axisLeft(yScale)
            .tickSize(-(dimensions.width))
            .tickFormat("")
            )

      //Y-Achsen-Beschrifzung
      svg.append("text")
    .attr("text-anchor", "end")
    .attr("transform", "rotate(-90)")
    .attr("fill", "grey")
    .attr("y", - 50)
    .attr("x", 0)
    .text("Prozent")

    

      //Tooltip-Circle
      var focus = svg
      .append('g')
      .append('circle')
      .style("fill", "#2770bc")
      .attr('r', 8.5)
      .style("opacity", 0);

      //Tooltip- Values
      var focusText = svg
      .append('g')
      .append('text')
      .style("opacity", 0)
      .attr("text-anchor", "left")
      .attr("alignment-baseline", "middle");

      svg
      .append('rect')
      .style("fill", "none")
      .style("pointer-events", "all")
      .attr('width', dimensions.width)
      .attr('height', dimensions.height)
   /**    .on('mouseover', mouseover)
      .on('mousemove', mousemove)
      .on('mouseout', mouseout); */

      const curve = curveLinear;

      const myLine = area()
      .x((d)=> (xScale(d.key)))
      .y0(0)
      .y1((d)=> (yScale(d.value)))
      .curve(curve)

    svg
      .selectAll("path")
      .data([data])
      .join("path")
      .attr("d", (value, index) => myLine(value))
      .attr("fill", "#db86ed")
      .attr("stroke", "#d043ee")
      .attr("stroke-width", "1.2" )
      .attr("class", "area")
      
      function mouseover() {
         
         focus.style("opacity", 1).transition()
         focusText.style("opacity",1).transition()
       }
       
       function mousemove() {
         // recover coordinate we need
         var x0 = xScale.invert(mouse(this)[0]);
         const bisect = bisector(d => new Date(d.key)).left;
         const idx = bisect(filtered, x0, 1);
         const selectedData = filtered[idx];
         console.log( selectedData);
         
         focus
         .transition()
         .duration(80)
         .attr("cx", xScale(new Date (selectedData.key)))
         .attr("cy", yScale(selectedData.value))
         

         focusText
         .transition()
         .duration(80)
            .text(selectedData.key  + " : " + selectedData.value + "%")
            .attr("x", xScale(new Date (selectedData.key)))
            .attr("y", yScale(selectedData.value) - 30)
            
            
       /*  focus
           .attr("cx", xScale(selectedData.x))
           .attr("cy", yScale(selectedData.y))
         focusText
           .html("x:" + selectedData.x + "  -  " + "y:" + selectedData.y)
           .attr("x", selectedData.x)
           .attr("y", yScale(selectedData.y)) */
          
         }
       function mouseout() {
         focus.style("opacity", 0)
         focusText.style("opacity", 0)
       }


   }, [data, dimensions, property]);
    return (
     <div  className="IKTWarapperRef" ref={wrapperRef} >
        <svg ref={svgRef}>
          <g className="x-axis" />
          <g className="y-axis" />
        </svg>
      </div>
    );
    }
    
    export default AreaIKTBeschäftigung;
