import React, { useRef, useEffect, useState } from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, event, bisector, mouse, ticks} from 'd3';
import useResizeObserver from "./useReziseObserver";


function BarMobilNutzung({ data, property }) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);
   const [selection, setSelection] = useState([0, 1.5]);

   useEffect(() => {

    const svg = select(svgRef.current);
     
         if(!dimensions) return;
      
        console.log(data);

         const xScale = scaleLinear()
         .domain([0, 100])
        .range([0, dimensions.width]) //change
        
       
         const yScale =  scaleBand()
         
        .domain(data.map((value, index) => value.age))   //todo
        .range([0, dimensions.height - 100])  //change
        .padding(0.2)

        const xAxis = axisBottom(xScale);
            svg
          .select(".x-axis")
         
          //.attr("transform", "translate(0," + dimensions.height + ")")
         // .attr("transform", `translateY(${dimensions.height}px)rotate(-45)`)
         .remove()

          const yAxis = axisLeft(yScale)
          .tickSizeOuter(0)
          .tickSizeInner(0);
          svg
            .select(".y-axis")
            .style('font-size', '14')
            .style('color', 'grey')
            .call(yAxis);

            svg
          .selectAll(".bar")
          .data(data)
          .join("rect")
          .attr("class", "bar")
          .attr("fill", "grey")
          .style("margin-top", "10px")
          .attr('x', xScale(0))
          .attr('y', (s) => yScale(s.age))
          .attr('width', (s) => xScale(s.value))
          .attr('height', yScale.bandwidth)
          .on("mouseenter", (value, index) => {
            svg
              .selectAll(".tooltip")
              .data([value])
              .join(enter => enter.append("text"))
              .attr("class", "tooltip")
              .text(value.value + "%")
              .attr("x", xScale(value.value) - 30)
              .attr("y", yScale(value.age) + yScale.bandwidth() / 1.5)
              .attr("opacity", 1)
              .transition()
              .attr("fill", "white")
            })
            
            .on("mouseleave", () => 
              svg.select(".tooltip").remove()
              .transition()
   )
         
  }, [data, dimensions, property]);
  return (
   <div ref={wrapperRef} >
      <svg ref={svgRef}>
        <g className="x-axis" />
        <g className="y-axis" />
      </svg>
    </div>
);
}

export default BarMobilNutzung;
