import React, { useRef, useEffect, useState } from "react";
import { select, min, max, entries, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand} from 'd3';
import useResizeObserver from "./useReziseObserver";
import '../../styles/highlight.css'


function BarHighKeinInternet({data}) {

    const svgRef = useRef();
    const wrapperRef = useRef();
    const dimensions = useResizeObserver(wrapperRef);
 
    useEffect(() => {
 
     const svg = select(svgRef.current);
     var filterme; 
     if(!dimensions) return;
        
        for(var i = 0; i < data.length; i ++) {
            var filterme = data[i].gründe; 
             }
             var vales = entries(filterme);
             
             console.log(vales)

        const xScale = scaleLinear()
        .domain([0, 50])
       .range([0, dimensions.width])
       
        const yScale =  scaleBand()
       .domain(vales.map((value, index) => value.key))
       .range([0, dimensions.height])
       .padding(0.4)
    

       const xAxis = axisBottom(xScale);
            svg
          .select(".x-axis")
          .remove()

          const yAxis = axisLeft(yScale)
          .tickSizeOuter(0)
          .tickSizeInner(0);
          svg
            .select(".y-axis")
            .style('font-size', '14')
            .style('color', 'grey')
            .call(yAxis);

            svg
            .selectAll(".bar")
            .data(vales)
            .join("rect")
            .attr("class", "bar")
            .attr("fill", "grey")
            .attr('x', xScale(0))
            .attr('y', (s) => yScale(s.key))
            .attr('width', (s) => xScale(s.value))
            .attr('height', yScale.bandwidth)

}, [data, dimensions]);
return (
 <div className="chartHighKeinInternet" ref={wrapperRef}>
    <svg ref={svgRef}>
      <g className="x-axis" />
      <g className="y-axis" />
    </svg>
  </div>
);
}

export default BarHighKeinInternet;
