import React, { useRef, useEffect, useState } from "react";
import { select, min, max, entries, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand} from 'd3';
import useResizeObserver from "./useReziseObserver";
import '../../styles/highlight.css'


function BarHighInternet({ data}) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);

   useEffect(() => {

    const svg = select(svgRef.current);
    var filterme; 
    if(!dimensions) return;
    console.log(data);

    for(var i = 0; i < data.length; i ++) {
        var filterme = data[i].time; 
         }

         var vales = entries(filterme);

   //Sortieren nach Jahreszahl
   const minDate = new Date (min(vales, value => value.key));
   const maxDate = new Date (max(vales, value => value.key));     

   console.log(minDate);
   console.log(maxDate);

   const xScale = scaleTime()
     .domain([minDate,maxDate])   
    .range([1, dimensions.width]) 
  
    const yScale =  scaleLinear()
    .domain([0, 100])
     .range([dimensions.height, 0]);

   const xAxis = axisBottom(xScale).tickSize(0);
       svg
       .style('font-size', '10')
       .attr("transform", "translate(0," + dimensions.height-1 + ")")
       .style('color', 'white')
        .select(".x-axis")
        .call(xAxis);
    
     const yAxis = axisLeft(yScale)  
          svg
            .select(".y-axis")
            .remove()

          
            svg
          .selectAll(".bar")
          .data(vales)
          .join("rect")
          .attr("class", "bar")
          .attr("fill", "grey")
          .attr("x", (d)=> (xScale(new Date(d.key))))
          .attr("y", (d)=> (yScale(d.value)))
          .attr("height", (d)=> yScale(0) - yScale(d.value))
          .attr("width", dimensions.width / 6);

    
     /*     .on("mouseenter", (value, index) => {
            svg
              .selectAll(".tooltip")
              .data([value])
              .join(enter => enter.append("text"))
              .attr("class", "tooltip")
              .text(value[property][Object.keys(value[property])[0]] + "%")
              .attr("x", xScale(value[property][Object.keys(value[property])[0]]) - 30)
              .attr("y", yScale(value.geo) + yScale.bandwidth() / 1.4)
              .attr("opacity", 1)
              .transition()
              .attr("fill", "white")
            })
            
            .on("mouseleave", () => 
              svg.select(".tooltip").remove()
              .transition()
   )

   */
}, [data, dimensions]);
return (
 <div className="chartHigh" ref={wrapperRef}>
    <svg ref={svgRef}>
      <g className="x-axis" />
      <g className="y-axis" />
    </svg>
  </div>
);
}

export default BarHighInternet;
