import React, { useRef, useEffect, useState } from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, event, bisector, mouse, ticks, tickStep} from 'd3';
import useResizeObserver from "./useReziseObserver";


function BarMobilNutzung({ data, highlight }) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);
   const [selection, setSelection] = useState([0, 1.5]);

   useEffect(() => {

    const svg = select(svgRef.current);
     
         if(!dimensions) return;
      
         const xScale = scaleLinear()
         .domain([0, 100])
        .range([0, dimensions.width * 30 / 100]) //change
        
       
         const yScale =  scaleBand()
         
        .domain(data.map((value, index) => value.age))   //todo
        .range([0, dimensions.height - 20])  //change
        .padding(0.3)
        const xAxis = axisBottom(xScale);
            svg
          .select(".x-axis")
         
          //.attr("transform", "translate(0," + dimensions.height + ")")
         // .attr("transform", `translateY(${dimensions.height}px)rotate(-45)`)
         .remove()

          const yAxis = axisLeft(yScale);
          svg
            .select(".y-axis")
            .style('color', 'grey')
            .call(yAxis);

            svg
          .selectAll(".bar")
          .data(data)
          .join("rect")
          .attr("class", "bar")
          .attr("fill", "grey")
          .style("margin-top", "10px")
          .attr('x', xScale(0))
          .attr('y', (s) => yScale(s.age))
          .attr('width', (s) => xScale(s.value))
          .attr('height', yScale.bandwidth)
          .on("mouseenter", (value, index) => {
            svg
              .selectAll(".tooltip")
              .data([value])
              .join(enter => enter.append("text"))
              .attr("class", "tooltip")
              .text(value.value)
              .attr("x", xScale(index) * 20)
              .attr("y", xScale(index) * 30)
              .attr("text-anchor", "middle")
              .transition()
              .attr("opacity", 1)
              
            })
            .on("mouseleave", () => svg.select(".tooltip").remove())
              .transition()
              
         
  }, [data, dimensions]);
  return (
   <div ref={wrapperRef} >
      <svg ref={svgRef}>
        <g className="x-axis" />
        <g className="y-axis" />
      </svg>
    </div>
);
}

export default BarMobilNutzung;
