import React, { useRef, useEffect, useState} from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, event, bisector, mouse, ticks} from 'd3';
import '../../styles/charts.css'

function ChartVorlage({ minDate, maxDate, dimensions}) {

    const xScale = scaleTime()
          .domain([minDate,maxDate])
          
        // .domain([0, data.length-1]) //0 zu 0 ; 6 zu 200
         .range([0, dimensions.width]) //change
        
      const yScale =  scaleLinear()
         .domain([0, 100])   //todo
         .range([dimensions.height, 30]);  //change

      
      const yAxis = axisLeft(yScale);   
      svg
      .select('.y-axis')
      .style('color', 'grey')
      .style('font-size', '14')
      .call(yAxis);

      //X-Achsen-Beschriftung
      svg.append("text")
      .attr("text-anchor", "end")
      .attr("fill", "grey")
      .attr("x", dimensions.width)
       .attr("y", dimensions.height  + 50)
       .text("Jahr");

     const xAxis = axisBottom(xScale)
     svg
      .select(".x-axis")
      .style('transform', `translateY(${dimensions.height}px)`)
      .style('color', 'grey')
      .style('font-size', '14')
      .style('stroke-width', 1)
      .call(xAxis);
      
      //Grids
      var grids = svg.append("g")
         .attr("class", "grid")
         .attr('transform', 'translate(50,50')
         .call(axisLeft(yScale)
            .tickSize(-(dimensions.width))
            .tickFormat("")
            )

      //Y-Achsen-Beschrifzung
      svg.append("text")
    .attr("text-anchor", "end")
    .attr("transform", "rotate(-90)")
    .attr("fill", "grey")
    .attr("y", - 50)
    .attr("x", 0)
    .text("Prozent")

   


}

export default ChartVorlage;
