import React, { useRef, useEffect, useState } from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, event, bisector, mouse, ticks, entries, arc, schemeDark2, scaleOrdinal, pie} from 'd3';
import useResizeObserver from "./useReziseObserver";


function PieMobilNutzung({ data, property }) {
    const svgRef = useRef();
    const wrapperRef = useRef();
    const dimensions = useResizeObserver(wrapperRef);
    const [selection, setSelection] = useState([0, 1.5]);
 
    useEffect(() => {
        const svg = select(svgRef.current);
        var filtered = data.filter(a=>a.geo==property);
       // console.log(filtered);

        if(!dimensions) return;
        var width = dimensions.width /1.5 ;
        var outerRadius = width/2;
        var innerRadius = 0;

        var pied = pie().value((d) => d.time[Object.keys(d.time)[0]])(filtered);

        var color = scaleOrdinal()
        .domain(pied)
        .range(["#bdbdbd","#969696","#737373","#525252","#252525"])

        function update(pied) {

          // map to data
          var u = svg.selectAll("path")
            .data(pied)
        
          // Build the pie chart: Basically, each part of the pie is a path that we build using the arc function.
          u
            .enter()
            .append('path')
            .merge(u)
            .transition()
            .duration(1000)
            .attr('d', arc()
              .innerRadius(innerRadius)
              .outerRadius(outerRadius)
            )
            .attr('fill', color)
            .attr("stroke", "white")
            .style("stroke-width", "2px")
            .style("opacity", 1)
        
          // remove the group that is not present anymore
          u
            .exit()
            .remove()
        
        }
        
        // Initialize the plot with the first dataset
        update(pied)

      /*  // Kreis zeichnen
        var arcs = svg.selectAll(".newPie")
        .data(pied)
        .join(enter => enter.append("path"), update => update.pathUpdate)
        .attr("class", "newPie")
        .attr("transform", "translate(" + outerRadius + "," + outerRadius + ")")
        
        var pathUpdate = svg.attr("d", arcd);

        var arcd = arc()
        .innerRadius(innerRadius)
        .outerRadius(outerRadius)

        arcs
      
        .join("d")
        .attr("d", arcd)
        //Farbschema               
        var color = scaleOrdinal()
        .domain(pied)
        .range(["#bdbdbd","#969696","#737373","#525252","#252525"])


        arcs.append("path")
        .attr("d", arcd)
        .attr("fill", function(d, i) { return color(i) })
        
        //Labels
        arcs.append("text")
        .attr("transform", function(d) {
        return "translate(" + arcd.centroid(d) + ")"})
        .attr("text-anchor", "middle")
        .text(function(d) {return d.data.ed})
        .attr("fill", "white")

        arcs.on("mouseover", function(d) {
           arcs.selectAll(".tooltip")
           .attr("transform", function(d) {
            return "translate(" + arcd.centroid(d) + ")"})
              .data([filtered])
              .join(enter => enter.append("text"))
              .attr("class", "tooltip")
             .text(d.value + "%")
             .attr("x", -13)
             .attr("y", 45 + "%")
            // .attr("x", mouse(this)[0] - 50)
            // .attr("y", mouse(this)[1] - 10)
          //  .attr("stroke", "blue")
           //.attr("x", mouse(this)[0])
          // .attr("y", mouse(this)[1])
           
             
             })
        .on("mouseout", function(d) {
            arcs.select(".tooltip").remove()
  })
        */
    }, [data, dimensions, property]);


return (
    <div ref={wrapperRef} >
       <svg ref={svgRef}>
       </svg>
     </div>
 );
 }
 
 export default PieMobilNutzung;
