import React, { useEffect, useState } from "react";
import DropDown from '../DropDown'
import ToggleSlider from '../ToggleSlider'
import LineChartContainer from './LineChart'
import '../../styles/charts.css'


export default function Zugangsdichte() {

  const [bbEpisodes, setBbEpisodes] = useState([]);
  const [data, setData] = useState([]);
  const [dataValue, setdataValue] = useState([]);
  const [highlight, setHighlight] = useState();

  useEffect(() => {
    fetch("http://ec.europa.eu/eurostat/wdds/rest/data/v2.1/json/de/isoc_ci_in_h?hhtyp=TOTAL&precision=1&geo=EU28&time=2007&time=2008&time=2009&time=2010&time=2011&time=2012&time=2013&time=2014&time=2015&time=2016&time=2017&time=2018&time=2019")
      .then(response => response.ok && response.json())
      .then(datazugang => {
        setData(datazugang.dimension.time.category.label);
        setdataValue(datazugang);
      //  setData(
      //    datazugang.sort((a, b) => a.time.localeCompare(b.time))
      //  );
      console.log(datazugang.value);
      })
      .catch(console.error);
  }, []);


    return (
        <div className="chart1">
        <div className="Zugangsdichte">     
        </div>
        
        <React.Fragment>
        <div className="elementsWrap">
        <div className="buttonWrap">
                    
                  <button className="btn-default" >alle</button>
                    <button className="btn-primary" >Breitband</button>
                    <button className="btn-secondary" >keine</button>
                    
                </div>
     
                </div>      
        <LineChartContainer highlight={highlight} data={data} />  
        </React.Fragment>
        <ToggleSlider textLinks={"Haushalte"} textRechts={"Unternehmen"}></ToggleSlider> />
            
        
        <div className="infobox">
        <p className="box1">At vero eos et accusam et justo duo dolores et ea rebum. Stet clita kasd gubergren, 
                    no sea takimata sanctus est Lorem ipsum dolor sit amet. At vero eos et accusam et justo duo
