import React, { useRef, useEffect, useState } from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, event, bisector, mouse, ticks, entries, arc} from 'd3';
import useResizeObserver from "./useReziseObserver";


function PieMobilNutzung({ data, highlight }) {
    const svgRef = useRef();
    const wrapperRef = useRef();
    const dimensions = useResizeObserver(wrapperRef);
    const [selection, setSelection] = useState([0, 1.5]);
 
    useEffect(() => {

        var radius = Math.min(dimensions.width, dimensions.height) / 2

        var color = scaleOrdinal()
            .domain(data)
            .range(schemeSet2);

            var pie = pie()
            .value((d) => d.value)

            var data_ready = pie(entries(data))

            var arcGenerator = arc()
    	        .innerRadius(0)
                .outerRadius(radius)

                svg
                .selectAll('mySlices')
                .data(data_ready)
                .enter()
                .join('path')
                    .attr('d', arcGenerator)
                    .attr('fill', function(d){ return(color(d.data.ed)) })
                    .attr("stroke", "black")
                    .style("stroke-width", "2px")
                    .style("opacity", 0.7)

                    svg
                    .selectAll('mySlices')
                    .data(data_ready)
                    .enter()
                    .append('text')
                    .text(function(d){ return "grp " + d.data.ed})
                    .attr("transform", function(d) { return "translate(" + arcGenerator.centroid(d) + ")";  })
                    .style("text-anchor", "middle")
                    .style("font-size", 17)


    }, [data, dimensions]);


return (
    <div ref={wrapperRef} >
       <svg ref={svgRef}>
       </svg>
     </div>
 );
 }
 
 export default PieMobilNutzung;
