import React, { useRef, useEffect, useState } from "react";
import { select, line, curveCardinal, axisBottom, scaleLinear } from 'd3';
import '../../styles/charts.css'

//dynamische Daten

function LineCharts() {
   const [data, setData] = useState([25, 30, 45, 60, 20, 65, 75]);
   const svgRef = useRef();
   
   //Wird einmal aufgerufen, wenn DOM-Element gerendert wird, und wenn sich Elemente im Array verändern
   useEffect(() => {
      const svg = select(svgRef.current);
      const xScale = scaleLinear()
         .domain([0, data.length - 1]) //0 zu 0 ; 6 zu 200
         .range([0, 300])

      const yScale =  scaleLinear()
         .domain([0, 150])
         .range([150, 0]);  

      
      const xAxis = axisBottom(xScale);
         svg.select('.x-axis')
         .style('transform', 'translate(150px)')
         .call(xAxis);

      const myLine = line()
         .x((value, index) => xScale(index)) 
         .y(yScale)
         .curve(curveCardinal);
     /**  svg
      .selectAll('circle')
      .data(data)
      .join('circle')
      .attr("r", value => value)
      .attr('cx', value => value * 2)
      .attr("cy", value => value * 2)
      .attr("stroke", 'red');
      svg
      .selectAll("path")
      .data([data])
      .join("path")
      .attr('d', myLine)
      .attr('fill', 'none')
      .attr("stroke", "blue");*/
   }, [data]);
   return <React.Fragment>
            <svg ref={svgRef}>
               <g className='x-axis' />
              
            </svg>
            <br/>
            <button onClick={() => setData(data.map(value => value + 5))}>
              Update data
            </button>
            <button onClick={() => setData(data.filter(value => value < 35))}>
              Filter data
            </button>
         </React.Fragment>
}

export default LineCharts;
