import React, { useRef, useEffect, useState } from "react";
import { select, axisBottom, axisLeft, scaleLinear, scaleBand, scaleOrdinal } from 'd3';
import '../../styles/charts.css'

//dynamische Daten

function LineCharts() {
   const [data, setData] = useState([25, 30, 45, 60, 20, 65, 75]);
   const svgRef = useRef();
   
   //Wird einmal aufgerufen, wenn DOM-Element gerendert wird, und wenn sich Elemente im Array verändern
   useEffect(() => {
      const svg = select(svgRef.current);

      var type = scaleOrdinal()
        .domain(['.bar'])
        .range(['red'])
        .unknown("blue");

      const xScale = scaleBand()
         .domain([0, 1, 2, 3, 4, 5, 6]) //0 zu 0 ; 6 zu 200
         .range([0, 1000])
         .padding(0.5);

      const yScale =  scaleLinear()
         .domain([10, 100])
         .range([500, 0]);  

      
         const colorScale =  scaleLinear()
         .domain([75, 100, 100])
         .range(["green", "orange", "red"])
         .clamp(true);  

      const yAxis = axisLeft(yScale);   
      svg.select('.y-axis')
         .style('transform', 'translateX(0px)')
         .call(yAxis);

     const xAxis = axisBottom(xScale).ticks(data.length);
     /**  svg
      .selectAll('circle')
      .data(data)
      .join('circle')
      .attr("r", value => value)
      .attr('cx', value => value * 2)
      .attr("cy", value => value * 2)
      .attr("stroke", 'red');*/
    svg
    .selectAll(".bar")
    .data(data)
    .join("rect")
    .attr("class", "bar")
    .attr("fill", type)
    .style("transform", "scale(1, -1") //Animation: Auf den KOpf stellen!
    .attr("x", (value, index) => xScale(index))
    .attr("y", -500) //Animation: nach unten, weil die Bars gedreht wurden
    .attr("width", xScale.bandwidth())
    .on("mouseenter", (value, index) => {
       svg //Tooltip
       .selectAll(".tooltip")
       .data([value])
       .join(enter => enter.append("text").attr("y", yScale(value) -4))
       .attr("class", "tooltip")
       .text(value)
       .attr("x", xScale(index) + xScale.bandwidth() / 2 )
       
       .attr("text-anchor", "middle")
       .transition()
       .attr("y", yScale(value) - 8)
       .attr("opacity", 1);
    })
    .on("mouseleave", () => 
    svg
    .select(".tooltip").remove())
    .transition()
    .attr("fill", colorScale)
    .attr("height", value =>500 - yScale(value))
   }, [data]);
   return <React.Fragment>
            <svg ref={svgRef} width="1000" height="500">
               <g className='x-axis' />
               <g className="y-axis" />
            </svg>
            <br/>
            <br/>
            <br/>
            <button onClick={() => setData(data.map(value => value + 5))}>
              Update data
            </button>
            <button onClick={() => setData(data.filter(value => value < 35))}>
              Filter data
            </button>
            
         </React.Fragment>
}

export default LineCharts;
