import React, { useEffect, useState } from "react";
import LineChartContainer from './MobilnutzungChart'
import BarChartContainer from './MobilChartAlter'
import PieChartContainer from './MobilChartBildung'
import NumberContainer from './MobilChartGeschlecht'
import '../../styles/charts.css'

function LivingOnline() {
  
  const [data, setData] = useState([]);
  const [dataAge, setDataAge] = useState([]);
  const [dataB, setDataB] = useState([]);
  const [dataG, setDataG] = useState([]);
  const [property, setProperty] = useState("Europäische Union");
  const [colorline, setColor] = useState("#2671bb");
  const [number, setNumber] = useState(1);
  const genF = "Frauen";
  const genM = "Männer";
  


  useEffect(() => {
    fetch("https://webhooks.mongodb-stitch.com/api/client/v2.0/app/dataviztest-bbcvc/service/viz/incoming_webhook/webhook1")
      .then(response => response.ok && response.json())
      .then(datazugang => {
        setData(datazugang);
      })
      .catch(console.error);
  }, []);

  useEffect(() => {
    fetch("https://webhooks.mongodb-stitch.com/api/client/v2.0/app/dataviztest-bbcvc/service/viz/incoming_webhook/MN-Age")
      .then(response => response.ok && response.json())
      .then(datazugang1 => {
        setDataAge(
          datazugang1.sort((a, b) => a.age.localeCompare(b.age))
        );
      })
      .catch(console.error);
  }, [number]);

  useEffect(() => {
    fetch("https://webhooks.mongodb-stitch.com/api/client/v2.0/app/dataviztest-bbcvc/service/viz/incoming_webhook/MN-Edu")
      .then(response => response.ok && response.json())
      .then(datazugang2 => {
        setDataB(datazugang2);
      })
      .catch(console.error);
  }, [number]);

  useEffect(() => {
    fetch("https://webhooks.mongodb-stitch.com/api/client/v2.0/app/dataviztest-bbcvc/service/viz/incoming_webhook/MN-Gen")
      .then(response => response.ok && response.json())
      .then(datazugang3 => {
        setDataG(datazugang3);
      })
      .catch(console.error);
  }, [number]);


  return (
    <React.Fragment>
      <div className="chart2">
        <div className="HeadlineWrapp">
          <div className="HeadMobildichte">
            <div className ="MobildichteHeadline">
              <h1>Mobilnutzung</h1>
            </div>
            <div className ="MobildichteText">
              <p> Die Möglichkeiten zur Nutzung des Internets sind vielvältig. Die <b>mobile Internetzung</b> bezeichnet die Nutzung des Internets an anderen Orten als zu Hause oder am Arbeitsplatz, zum Beispiel auf tragbaren Computern oder <b>mobilen Geräten</b> über mobile oder drahtlose Verbindungen. </p>
            </div>
            </div>
            <select value={property} onChange={e => setProperty(e.target.value)}>
              {data.map(dataZugang => (
                   <option key={dataZugang.geo}>{dataZugang.geo}</option>
              ))}
            </select>
          </div>
          <div className="chartBody">
             <LineChartContainer property={property} data={data} colorline={colorline}/>
          </div>
          <div className="Jahre">
              <button onClick={() => setNumber(0) } type="submit" class="jButton">2012</button>
              <button onClick={() => setNumber(1) } type="submit" class="jButton">2013</button>
              <button onClick={() => setNumber(2) } type="submit" class="jButton">2014</button>
              <button onClick={() => setNumber(3) } type="submit" class="jButton">2015</button>
              <button onClick={() => setNumber(4) } type="submit" class="jButton">2016</button>
              <button onClick={() => setNumber(5) } type="submit" class="jButton">2017</button>
              <button onClick={() => setNumber(6) } type="submit" class="jButton">2018</button>
              <button onClick={() => setNumber(7) } type="submit" class="jButton">2019</button>
            </div>
          <div className="specialCharts">
           
            <div className="BarAge">
              <h5>Alter</h5>
                <BarChartContainer property={property} data={dataAge} number={number}/>
            </div>
            <div className="PieEd">
            <div className="ChartEd">
              <h5>Bildung</h5>
              <PieChartContainer property={property} data={dataB} number={number}/>
              </div>
              </div>
          <div className="MaleFemale">
          <div className="NumberHeader">
            <h5>Gender</h5>
          </div>
          <div className="Number">
              <div className="Frau">
                  <NumberContainer property={property} data={dataG} gen={genF} number={number}/>
                  <div className="frauenName">
                    <p className="gender">Frauen</p>
                    <p className="alter">16 bis 74 Jahre</p>
                  </div>
              </div>
              <div className="Mann">
                 <NumberContainer property={property} data={dataG} gen={genM} number={number}/>
              <div className="männerName">
                <p className="gender">Männer</p>
                <p className="alter">16 bis 74 Jahre</p>
              </div>
              </div>
          </div>
            
          </div>
              </div>
          <div className="NutzungTextWrap">
            <div className="NutzungText"></div>
                In der Europäischen Union stieg die Anteil von Personen, die angaben das Internet unterwegs zu nutzen, um 39 Prozentpunkte an. 
                2012 betrug der Anteil 36%, während es 2019 75% waren. Unterschiede zeigen sich besonders in den Altersklassen. 2012 nutzten 63% der Personen im Alter von 16 bis 24 Jahren eine mobile Internetverbindung. Im Jahr 2019 waren es bereits 95%. 
                Personen in der Altersgruppe 55 - 74 Jahre nutzten mobile Internetzugänge 2012 nur zu 14% und 2019 zu 52%, wobei die Zunahme der Nutzung in beiden Altersgruppen um ähnlich viele Prozentpunkte stieg. <br></br> in der Altersgruppe 16 bis 24 JahreIm Vereinigten Königreich gaben 2019 100% der Personen im Alter zwischen 16 bis 24 Jahren an, über mobile Geräte das Internet zu benutzen.

                Die Nutzung von mobilem Internet hängt außerdem vom Grad der Bildung ab. Personen, mit niedriger formaler Bildung nutzen diese Art des Internetzugangs weniger als es bei Personen mit hoher, formaler Bildung der Fall ist.


          </div>
          </div>
      </React.Fragment>
  )

}

export default LivingOnline;
