import React, { useEffect, useState } from "react";
import LineChartContainer from './ZugangsdichteChart'
import '../../styles/charts.css'


export default function Zugangsdichte() {

  const [colorline, setColor] = useState("#2671bb");
  const [colorline2, setColor2] = useState("#da26da");
  const [data, setData] = useState([]);
  const [property, setProperty] = useState("Europäische Union");
  const [change, setChange] = useState("ZD");
  const [value, setValue] = useState("alle");

  const handleAlle = () => {
    setChange("ZD");
    setColor("#2671bb");
    setValue("alle");
  }  
  const handleBB = () => {
    setChange("ZD-BB");
    setColor2("#da26da");
    setValue("BB");
  }


  useEffect(() => {
    fetch(`https://webhooks.mongodb-stitch.com/api/client/v2.0/app/dataviztest-bbcvc/service/viz/incoming_webhook/ZD`)
      .then(response => response.ok && response.json())
      .then(datazugang => {
        setData(datazugang);
      })
      .catch(console.error);
  }, [change, colorline]);

    return (
        <div className="chart1">
        <div className="Zugangsdichte">     
        </div>
        
        <React.Fragment>
        <div className="elementsWrap">
        <div className="buttonWrap">
                    
                  <button className="btn-default" onClick={handleAlle} >alle</button>
                    <button className="btn-primary" onClick={handleBB}>Breitband</button>
                  
                    
                </div>
      <select value={property} onChange={e => setProperty(e.target.value)}>
        {data.map(dataZugang => (
          <option key={dataZugang.geo}>{dataZugang.geo}</option>
        ))}
      </select>
                </div>      
        <LineChartContainer property={property} data={data} colorline={colorline} colorline2={colorline2} value={value}/>  
        </React.Fragment>
        
        <div className="infobox">
        <p className="box1">Bulgarien, Rumänien, Zypern, Griechenland, Portugal und Kroatien verzeichneten einen <b>starken Anstieg</b> der Internetzugangsdichte. Die Haushalte mit Internetzugang stiegen in Bulgarien in den <b>letzten 10 Jahren</b> beispielsweise um <b>42 Prozentpunkte</b>.</p>
        
        <div className="numberBox"><p className="numberAlle">90%</p>
                    <p className="numberText2">der Haushalte in der <b>Europäischen Union</b> haben Zugang zum Internet. 2010 waren es noch <b>70%</b>.</p>
                    </div>

        <div className="numberBox"><p className="numberBB">89%</p>
                    <p className="numberText2">der Haushalte haben einen <div className="high">Breitbandzugang.</div> Dies entspricht einer Erhöhung um <div className="high">25 Prozentpunkten</div> in den letzten  9 Jahren.</p></div>
         <p className="box1">Die Niederlande verzeichneten 2019 mit <div className="high">98%</div> den höchsten Anteil an Breitbandzugängen. In Rumänien wurde ein Anstieg der Haushalte mit Breitbandzugang in den letzten 9 Jahren um<div className="high">59 Prozentpunkte</div>registriert. 2010 waren es 23% der Haushalte.</p>
                    </div>
        </div>
    );

}
