    const maxDate = new Date (max(objectsIKT.gen, value => value.key));     

    console.log(minDate);
    console.log(maxDate);


      const xScale = scaleTime()
          .domain([minDate,maxDate])
         .range([0, dimensions.width])
        
      const yScale =  scaleLinear()
         .domain([0, 100])
         .range([dimensions.height, 30]);

      
         const yAxis = axisLeft(yScale);   
         svg
         .select('.y-axis')
         .style('color', 'grey')
         .style('font-size', '14')
         .call(yAxis);
   
        const xAxis = axisBottom(xScale)
        svg
         .select(".x-axis")
         .join(".x-axis")
         .style('transform', `translateY(${dimensions.height}px)`)
         .style('color', 'grey')
         .style('font-size', '14')
         .style('stroke-width', 1)
         .call(xAxis);
   
         //Grids
         var grids = svg.select("g").append("g")
            .join("g")
            .attr("class", "grid")
            .attr('transform', 'translate(0,' + -dimensions.height + ')')
            .call(axisLeft(yScale)
               .tickSize(-dimensions.width)
               .tickFormat("")
               )
         
         //X-Achsen-Beschriftung
         svg.select("text").join("text")
         .attr("text-anchor", "end")
         .attr("fill", "grey")
         .attr("x", dimensions.width)
          .attr("y", + dimensions.height/ 8)
          .attr("font-weight", "bold")
          .text("Jahr");      
         //Y-Achsen-Beschrifzung
         svg.append("text")
         .attr("text-anchor", "end")
         .attr("transform", "rotate(-90)")
         .attr("fill", "grey")
         .attr("y", - 50)
         .attr("x", 0)
         .text("Prozent")

