                .attr('y1', 0)
                .attr('y2', dimensions.height);

                function mousemove() {
                  var x0 = xScale.invert(mouse(this)[0]);
                  const bisect = bisector(d => new Date(d.key)).left;
                  const idx = bisect(valesFrau, x0, 1);
                  const selectedDataFrau = valesFrau[idx];
                  console.log( selectedDataFrau);

                  const idxM = bisect(valesMann, x0, 1);
                  const selectedDataMann = valesMann[idxM];
                  console.log( selectedDataMann);
                  
                  focus
                  .transition()
                  .duration(80)
                  .attr("x1", xScale(new Date (selectedDataFrau.key)))
                  .attr("x2", xScale(new Date (selectedDataFrau.key)))
                  .attr("y1", yScale(selectedDataFrau.value) - dimensions.height / 1.2)
                  .attr("y2",  dimensions.height + 20)
                  
                  focusMann
