                  .attr("x", xScale(new Date (selectedDataFrau.key)) + dimensions.width/20)
                  .attr("y", dimensions.height/2.1 )
                      
                  focusBox
                  .transition()
                  .duration(80)
                  .attr("x", xScale(new Date (selectedDataFrau.key)) + 20)
                  .attr("y", dimensions.height/3)

         }
       
                function mouseout() {
                  focus.style("opacity", 0)
                  focusMann.style("opacity", 0)
                  focusFrau.style("opacity", 0)
                  focusBox.style("opacity", 0)
