*/
      const curve = curveLinear;

      Tooltip(svg, valesFrau, valesMann, color1, color2, dimensions, xScale, yScale, value2, value1, value1 );

      const myArea = area()
      .x((d)=> (xScale(new Date(d.key))))
      .y0(dimensions.height)
      .y1((d)=> (yScale(d.value)))
      .curve(curve)

        svg
         .selectAll(".newArea")
         .data([valesMann])
         .join("path")
         .attr("class", "newArea")
         .transition()
         .duration(500)
         .attr("d", (value, index) => myArea(value))
         .transition()
         .attr("fill", color1)
         
        
         svg
         .selectAll(".newerArea")
         .data([valesFrau])
         .join("path")
         .attr("class", "newerArea")
         .transition()
         .duration(500)
         .attr("d", (value, index) => myArea(value))
         
         .attr("fill", "none")
         .attr("fill", color2)

   }, [data, dimensions, property]);
    return (
     <div  className="IKTWarapperRef" ref={wrapperRef} >
        <svg ref={svgRef}>
          <g className="x-axis" />
          <g className="y-axis" />
        </svg>
      </div>
    );
    }
    
    export default AreaIKTBeschäftigung;
