import React, { useRef, useEffect} from "react";
import { select, scaleOrdinal, pie, arc, } from 'd3';
import useResizeObserver from "./useReziseObserver";
import '../../styles/highlight.css'


function BarHighInternet({ data, number}) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);

   useEffect(() => {

    const svg = select(svgRef.current);
  
    if(!dimensions) return;
    const xScale = scaleLinear()
    .domain([0, 100])
   .range([0, dimensions.width])
   
    const yScale =  scaleBand()
   .domain(data.map((value, index) => value.age))
   .range([0, dimensions.height - 100])
   .padding(0.2)

   var color = scaleOrdinal()
   .domain(data.map((value, index) => value.age))
   .range(["#2770bc", "#6810d4", "#ca27cb"])

    const xAxis = axisBottom(xScale);
       svg
     .select(".x-axis")
     .remove()

     const yAxis = axisLeft(yScale)
     .tickSizeOuter(0)
     .tickSizeInner(0);
     svg
       .select(".y-axis")
       .style('font-size', '14')
       .style("font-weight", "bold")
       .style('color', 'grey')
       .call(yAxis);

    

   
}, [data, dimensions]);
return (
 <div className="chartHigh" ref={wrapperRef}>
    <svg ref={svgRef}>
      <g className="x-axis" />
      <g className="y-axis" />
    </svg>
  </div>
);
}

export default BarHighInternet;
