import React from 'react';
import Header from '../Header'
import Content from '../Content'
import Content2 from '../Content2'
import Map from '../Map'
import Highlight from '../Highlight'
import Zugangsdichte from '../charts/Zugangsdichte'
import MobilNutzung from '../charts/Mobilnutzung'
import NutzungsGründe from '../charts/Nutzungsgründe'
import IktBeschäftigung from '../charts/IKTBeschäftigung'
import HighInternetnutzung from '../charts/HighlightInternet'
import '../../styles/content.css';

export default function Menschen() {

    return(
    <div className="Wrap1">
        <div className="placeholder1"></div>
        <div className="placeholder2"></div>
        <div className="placeholder3"></div>

        <Highlight
        überschrifth3="Übersicht "
        überschrift="Gründe, warum kein Internetzugang besteht" 
                   text="Im Jahr 2019 gaben 46% der Haushalte, die keinen Zugang zum Internet haben, an, das Internet nicht als nützlich anzusehen. Der zweite Hauptgrund ist, dass den betroffenen Personen die erforderlichen Kenntnisse fehlten (44%)."
                   hichart1={
                    < HighInternetnutzung/>
                         }
                   >
       
        </Highlight>
        
        <Header überschrift1="Digitale Wirtschaft" 
                überschrift2="und Gesellschaft"
                boldText="Für viele Menschen in der EU ist die Nutzung des Internets zu einem wichtigen Teil ihres täglichen Lebens geworden."
                normalText="Heutzutage verbringen Menschen einen erheblichen Teil ihrer Zeit aus verschiedenen Gründen online, sei es bei der Arbeit, in der Schule, zu Hause oder unterwegs. Diese digitale Zusammenfassung bietet leicht verständliche Statistiken zu verschiediedenen digitalen Themen und präsentiert sie anhand von interaktiven Datenvisualisierungen. "
                
                mapchart={
                <Map></Map>
                }
        ></Header>
        <Content überschrift="Haushalte - Internet-Zugangsdichte"
                 text="Die Internet- und Kommunikationstechnik für die breite Öffentlichkeit hat sich in den letzten Jahren erheblich verbessert. In allen EU-Mitgliedstaaten sind Breitbandnetze die am stärksten verbreitete Form des Internetzugangs. Island, Norwegen und die Niederlande hattem mit 98% im Jahr 2019 den höchsten Anteil an Internetzugängen, während Bulgarien mit 75% die niedrigsten Quoten besaß. "
                 highlightNumber="79%"
                 erklärungsText="der Personen zwischen 16 und 74 Jahren in der EU gaben an, das Internet häufig (durchschnittlich jeden Tag oder fast jeden Tag) zu benutzen."
        ></Content>
        <Content2 chart={
            <Zugangsdichte infoBoxAlle={["Bulgarien, Rumänien, Zypern, Griechenland, Portugal und Kroatien verzeichneten einen starken der Internetzugangsdichte. Die Haushalte mit Internetzugang stiegen in Bulgarien in den letzten 10 Jahren beispielsweise um 42 Prozentpunkte."]}
                            numberAlle="90%"
                            numberAlleText="der Haushalte in der Europäischen Union haben Zugang zum Internet. 2010 waren es noch 70%"
                            numberBB="89%"
                            numberBBText="der Haushalte haben einen Breitbandzugang. Dies entspricht einer Erhöhung um 25 Prozentpunkten in den letzten  9 Jahren."
                            infoBoxBB="Die Niederlande verzeichneten 2019 mit 98% den höchsten Anteil an Breitbandzugängen. In Rumänien wurde ein Anstieg der Haushalte mit Breitbandzugang in den letzten 9 Jahren um 59 Prozentpunkte registriert. 2010 waren es 23% der Haushalte."
            ></Zugangsdichte>
                           
        } chart2={
            <Mobilnutzung headline="Mobilnutzung"
            
            ></Mobilnutzung>
                 } chart3={
            <NutzungsGründe/>
                 } chart4 ={
            <IktBeschäftigung></IktBeschäftigung>
                }

        >
        </Content2>
    
        </div>


);
    }
