import React, { useRef, useEffect, useState} from "react";
import { select, line, curveCardinal, axisBottom, axisLeft, scaleTime, scaleLinear, scaleBand, min, max, ticks} from 'd3';
import '../../styles/charts.css'
import useResizeObserver from './useReziseObserver'

function LineCharts({ data, highlight }) {
   const svgRef = useRef();
   const wrapperRef = useRef();
   const dimensions = useResizeObserver(wrapperRef);
   
  // const [dat, setData] = useState([25, 30, 45, 60, 20, 65, 75]);
   //Wird einmal aufgerufen, wenn DOM-Element gerendert wird, und wenn sich Elemente im Array verändern
   useEffect(() => {

      const svg = select(svgRef.current);
         console.log(data);
      //const test = data.value[0];
     // console.log(test);
         if(!dimensions) return;
      
      //Sortieren nach Jahreszahl
     
      const minDate = new Date (min(data, value => value.time));
      const maxDate = new Date (max(data, value => value.time));
      
     

      console.log(minDate);
      console.log(maxDate);


      const xScale = scaleTime()
          .domain([minDate,maxDate])
        // .domain([0, data.length-1]) //0 zu 0 ; 6 zu 200
         .range([0, dimensions.width]) //change
        // .padding(0.5);
      const yScale =  scaleLinear()
         .domain([0, 100])   //todo
         .range([dimensions.height, 30]);  //change

      const yAxis = axisLeft(yScale);   
      svg
      .select('.y-axis')
      .call(yAxis);

     const xAxis = axisBottom(xScale)
     svg
      .select(".x-axis")
      .style('transform', `translateY(${dimensions.height}px)`)
      .call(xAxis)

      const myLine = line()
      .x((d)=> (xScale(new Date(d.time))))
      .y((d)=> (yScale(d.value) - dimensions.height))
      .curve(curveCardinal);
    svg
      .selectAll("path")
      .data([data])
      .join("path")
      .attr("d", value => myLine(value))
      .attr("fill", "none")
      .attr("stroke", "#2770bc")
      .attr("stroke-width", "5" )
      .on("mouseover", (value, index) => {
         svg
           .selectAll(".tooltip")
           .data([value])
           .join("text")
           .attr("class", "tooltip")
           .attr("stroke", "red")
           .text(value)
           console.log(value => value.value);
       })
       .on("mouseleave", () => svg.select(".tooltip").remove())

       var focus = svg.append("path")
        .attr("class", "focus")
        .style("display", "none");

        focus.append("line")
        .attr("class", "x-hover-line hover-line")
        .attr("y1", 0)
        .attr("y2", dimensions.height);

    focus.append("line")
        .attr("class", "y-hover-line hover-line")
        .attr("x1", dimensions.width)
        .attr("x2", dimensions.width);

    focus.append("circle")
        .attr("r", 7.5);

    focus.append("text")
        .attr("x", 15)
      	.attr("dy", ".31em");

         svg.append("rect")
         .attr("transform", "translate(" + margin.left + "," + margin.top + ")")
         .attr("class", "overlay")
         .attr("width", dimensions.width)
         .attr("height", dimensions.height)
         .on("mouseover", function() { focus.style("display", null); })
         .on("mouseout", function() { focus.style("display", "none"); })
         .on("mousemove", mousemove);
 
     function mousemove() {
       var x0 = xScale.invert(mouse(this)[0]),
           i = bisectDate(data, x0, 1),
           d0 = data[i - 1],
           d1 = data[i],
           d = x0 - d0.time > d1.time - x0 ? d1 : d0;
       focus.attr("transform", "translate(" + x(d.time) + "," + y(d.value) + ")");
       focus.select("text").text(function() { return d.value; });
       focus.select(".x-hover-line").attr("y2", dimensions.height - y(d.value));
       focus.select(".y-hover-line").attr("x2", dimensions.width + dimensions.width);
     }

   }, [data, dimensions]);
   return (
         <div ref={wrapperRef}>
            <svg ref={svgRef}>
               <g className='x-axis' />
               <g className="y-axis" />
            </svg>
            </div>
   );
}

export default LineCharts;
